import { useEffect, useMemo, useState } from "react";
import { Sun, Sunset, Moon, Clock, X, Plus, Trash2, Pencil, MoreVertical } from "lucide-react";
import Layout from "../components/Layout";
import { useAuth } from "../context/AuthContext";
import api from "../api/client";
import dayjs from "dayjs";

// --- Date Picker & Localization ---
import DatePicker, { registerLocale } from "react-datepicker";
import he from "date-fns/locale/he";
import "react-datepicker/dist/react-datepicker.css";
registerLocale('he', he); // Register Hebrew for the calendar
// ----------------------------------

function getGreeting(hour) {
    if (hour >= 5 && hour < 12) return "בוקר טוב";
    if (hour >= 12 && hour < 17) return "צהריים טובים";
    if (hour >= 17 && hour < 22) return "ערב טוב";
    return "לילה טוב";
}

export default function HomePage() {
    const { user, refreshUser } = useAuth();
    const [summary, setSummary] = useState(null);
    const [recentShifts, setRecentShifts] = useState([]);
    const [shiftTypes, setShiftTypes] = useState([]);

    // UI States
    const [isAddOpen, setIsAddOpen] = useState(false);
    const [isTipOpen, setIsTipOpen] = useState(false);
    const [activeMenuId, setActiveMenuId] = useState(null);

    // Form States
    const [selectedShiftCode, setSelectedShiftCode] = useState(null);
    const [selectedDate, setSelectedDate] = useState(new Date().toISOString().slice(0, 10));
    const [startTime, setStartTime] = useState("");
    const [endTime, setEndTime] = useState("");
    const [isOvertimeOpen, setIsOvertimeOpen] = useState(false);
    const [overtimeHours, setOvertimeHours] = useState("");
    const [overtimeRate, setOvertimeRate] = useState("");

    // Edit/Tip Logic
    const [tipAmount, setTipAmount] = useState("");
    const [tipShiftId, setTipShiftId] = useState(null);
    const [editShiftId, setEditShiftId] = useState(null);

    // Mapping helper
    const shiftTypeMap = useMemo(() => {
        const map = {};
        shiftTypes.forEach(t => { map[t.nameHe] = t; map[t.code] = t; });
        return map;
    }, [shiftTypes]);

    // Close menus on click outside
    useEffect(() => {
        const handleClick = () => setActiveMenuId(null);
        window.addEventListener('click', handleClick);
        return () => window.removeEventListener('click', handleClick);
    }, []);

    // Load Data
    useEffect(() => {
        refreshUser();
        refreshSummary();
        api.get("/shift-types").then(res => setShiftTypes(res.data));
    }, []);

    async function refreshSummary() {
        try {
            const [summaryRes, historyRes] = await Promise.all([api.get("/summary"), api.get("/history")]);
            setSummary(summaryRes.data);
            if (historyRes.data.items) setRecentShifts(historyRes.data.items.slice(0, 5));
        } catch (e) { console.error(e); }
    }

    // --- Handlers ---
    const handleShiftSelect = (shift) => {
        setSelectedShiftCode(shift.code);
        setStartTime(shift.defaultStart);
        setEndTime(shift.defaultEnd);
    };

    const handleEditShift = (shift) => {
        setEditShiftId(shift.id);
        setSelectedDate(shift.date); // Assumes YYYY-MM-DD from DB
        const t = shiftTypeMap[shift.shiftType];
        if (t) setSelectedShiftCode(t.code);
        setStartTime(shift.startTime?.slice(0, 5) || "");
        setEndTime(shift.endTime?.slice(0, 5) || "");
        setOvertimeHours(shift.overtimeHours || "");
        setOvertimeRate(shift.overtimeHourlyRate || "");
        setIsOvertimeOpen(!!shift.overtimeHours);
        setIsAddOpen(true);
    };

    async function handleCreateShift() {
        if (!selectedShiftCode || !selectedDate) return;
        try {
            const payload = {
                shiftCode: selectedShiftCode,
                date: selectedDate,
                startTime,
                endTime,
                overtimeHours: overtimeHours ? Number(overtimeHours) : 0,
                overtimeHourlyRate: overtimeRate ? Number(overtimeRate) : null,
            };

            if (editShiftId) await api.put(`/shifts/${editShiftId}`, payload);
            else await api.post("/shifts", payload);

            closeAddModal();
            refreshSummary();
            refreshUser();
        } catch { alert("שגיאה בשמירה"); }
    }

    async function handleAddTip() {
        if (!tipShiftId || !tipAmount) return;
        await api.post(`/shifts/${tipShiftId}/tip`, { tipAmount: Number(tipAmount) });
        setIsTipOpen(false); setTipShiftId(null); setTipAmount("");
        refreshSummary();
    }

    const closeAddModal = () => {
        setIsAddOpen(false);
        setEditShiftId(null);
        setSelectedShiftCode(null);
        setStartTime(""); setEndTime("");
        setIsOvertimeOpen(false);
        setOvertimeHours(""); setOvertimeRate("");
        setSelectedDate(new Date().toISOString().slice(0, 10));
    };

    // Styling Config
    const shiftConfig = {
        morning: { icon: Sun, bg: 'bg-orange-100', text: 'text-orange-600' },
        evening: { icon: Sunset, bg: 'bg-blue-100', text: 'text-blue-600' },
        night: { icon: Moon, bg: 'bg-indigo-100', text: 'text-indigo-600' },
        middle: { icon: Clock, bg: 'bg-teal-100', text: 'text-teal-600' }
    };

    return (
        <Layout>
            {/* Header */}
            <header className="mb-6 px-1">
                <h1 className="text-2xl font-bold text-slate-900">
                    {getGreeting(new Date().getHours())}, {user?.displayName || "אורח"}
                </h1>
                <p className="text-slate-500 font-medium mt-1">
                    {new Date().toLocaleDateString("he-IL", { weekday: "long", day: "numeric", month: "long" })}
                </p>
            </header>

            {/* Quick Stats Grid */}
            <section className="grid grid-cols-2 gap-3 mb-8">
                <div className="col-span-2 bg-slate-900 rounded-3xl p-6 text-white shadow-xl shadow-slate-200">
                    <p className="text-slate-400 text-sm font-medium mb-1">שכר חודשי צפוי</p>
                    <div className="text-4xl font-bold tracking-tight">₪{(summary?.expectedMonthSalary ?? 0).toFixed(0)}</div>
                </div>
                <div className="bg-white p-5 rounded-3xl shadow-sm border border-slate-100 flex flex-col justify-between">
                    <p className="text-slate-400 text-xs font-bold">שעות החודש</p>
                    <p className="text-2xl font-bold text-emerald-600">{(summary?.monthHours ?? 0).toFixed(1)}</p>
                </div>
                <div className="bg-white p-5 rounded-3xl shadow-sm border border-slate-100 flex flex-col justify-between">
                    <p className="text-slate-400 text-xs font-bold">טיפים</p>
                    <p className="text-2xl font-bold text-slate-800">₪{(summary?.totalTips ?? 0).toFixed(0)}</p>
                </div>
            </section>

            {/* Shift List */}
            <section className="pb-24">
                <h2 className="text-lg font-bold text-slate-800 mb-4 px-1">משמרות אחרונות</h2>
                <div className="flex flex-col gap-3">
                    {recentShifts.map((item) => {
                        const config = shiftConfig[item.shiftType?.toLowerCase()] || shiftConfig.middle;
                        const Icon = config.icon;

                        // Localization for Card
                        const dateObj = new Date(item.date);
                        const dayName = dateObj.toLocaleDateString('he-IL', { weekday: 'long' }); // יום ראשון
                        const dateShort = dateObj.toLocaleDateString('he-IL', { day: '2-digit', month: '2-digit' }); // 12/05

                        return (
                            <div key={item.id} className="bg-white rounded-2xl p-4 shadow-sm border border-slate-100 flex items-center gap-4">
                                {/* Date Column - New! */}
                                <div className="flex flex-col items-center justify-center min-w-[3.5rem] border-l pl-4 border-slate-100">
                                    <span className="text-[10px] font-bold text-slate-400">{dayName}</span>
                                    <span className="text-lg font-bold text-slate-800 leading-none mt-1">{dateShort}</span>
                                </div>

                                {/* Icon */}
                                <div className={`w-12 h-12 rounded-2xl flex items-center justify-center shrink-0 ${config.bg} ${config.text}`}>
                                    <Icon className="w-6 h-6" />
                                </div>

                                {/* Details */}
                                <div className="flex-1 min-w-0">
                                    <h3 className="text-base font-bold text-slate-900 truncate">
                                        {item.shiftType || "משמרת"}
                                    </h3>
                                    <p className="text-sm text-slate-500 font-medium">
                                        {item.startTime?.slice(0, 5)} - {item.endTime?.slice(0, 5)}
                                    </p>
                                </div>

                                {/* Action & Money */}
                                <div className="flex flex-col items-end gap-2">
                                    <span className="text-base font-bold text-slate-900">₪{item.salary.toFixed(0)}</span>

                                    <div className="flex items-center gap-2">
                                        {/* Larger Tip Button */}
                                        <button
                                            onClick={(e) => { e.stopPropagation(); setTipShiftId(item.id); setTipAmount(item.tip || ""); setIsTipOpen(true); }}
                                            className={`px-3 py-1.5 rounded-xl text-xs font-bold transition-colors ${item.tip > 0 ? 'bg-emerald-100 text-emerald-700' : 'bg-slate-100 text-slate-500 hover:bg-slate-200'}`}
                                        >
                                            {item.tip > 0 ? `+ ₪${item.tip}` : '+ טיפ'}
                                        </button>

                                        {/* Menu */}
                                        <div className="relative">
                                            <button
                                                onClick={(e) => { e.stopPropagation(); setActiveMenuId(activeMenuId === item.id ? null : item.id); }}
                                                className="p-2 -mr-2 text-slate-300 hover:text-slate-600 hover:bg-slate-50 rounded-xl"
                                            >
                                                <MoreVertical className="w-5 h-5" />
                                            </button>
                                            {activeMenuId === item.id && (
                                                <div className="absolute left-0 top-8 w-40 bg-white rounded-xl shadow-xl border border-slate-100 z-10 overflow-hidden">
                                                    <button onClick={(e) => { e.stopPropagation(); setActiveMenuId(null); handleEditShift(item); }} className="w-full px-4 py-3 text-sm font-medium text-slate-700 hover:bg-slate-50 flex items-center justify-end gap-2 border-b border-slate-50">
                                                        ערוך <Pencil className="w-4 h-4"/>
                                                    </button>
                                                    <button onClick={async (e) => { e.stopPropagation(); setActiveMenuId(null); if(window.confirm("למחוק?")) { await api.delete(`/shifts/${item.id}`); refreshSummary(); refreshUser(); } }} className="w-full px-4 py-3 text-sm font-medium text-red-600 hover:bg-red-50 flex items-center justify-end gap-2">
                                                        מחק <Trash2 className="w-4 h-4"/>
                                                    </button>
                                                </div>
                                            )}
                                        </div>
                                    </div>
                                </div>
                            </div>
                        );
                    })}
                </div>
            </section>

            {/* Floating Action Button (FAB) - Bottom Right */}
            <button
                onClick={() => setIsAddOpen(true)}
                className="fixed bottom-6 left-6 bg-slate-900 text-white w-14 h-14 rounded-full shadow-2xl shadow-slate-900/40 flex items-center justify-center hover:scale-105 active:scale-95 transition-all z-40"
            >
                <Plus className="w-7 h-7" />
            </button>

            {/* --- ADD SHIFT MODAL (Bottom Sheet style) --- */}
            {isAddOpen && (
                <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-sm z-50 flex items-end sm:items-center justify-center transition-all">
                    {/* Modal Content */}
                    <div className="bg-white w-full max-w-lg rounded-t-[32px] sm:rounded-3xl p-6 sm:p-8 max-h-[90vh] overflow-y-auto animate-slide-up shadow-2xl">

                        {/* Header */}
                        <div className="flex items-center justify-between mb-6">
                            <h2 className="text-xl font-bold text-slate-900">
                                {editShiftId ? 'עריכת משמרת' : 'הוספת משמרת'}
                            </h2>
                            <button onClick={closeAddModal} className="p-2 bg-slate-100 rounded-full text-slate-500 hover:bg-slate-200">
                                <X className="w-5 h-5" />
                            </button>
                        </div>

                        {/* Date Picker - Large & Localized */}
                        <div className="mb-6">
                            <label className="block text-sm font-bold text-slate-700 mb-2">תאריך</label>
                            <div className="bg-slate-50 rounded-2xl p-1 border border-slate-200">
                                <DatePicker
                                    selected={selectedDate ? new Date(selectedDate) : null}
                                    onChange={(date) => {
                                        if (date) {
                                            const offsetDate = new Date(date.getTime() - (date.getTimezoneOffset() * 60000));
                                            setSelectedDate(offsetDate.toISOString().slice(0, 10));
                                        }
                                    }}
                                    locale="he" // Force Hebrew
                                    dateFormat="dd/MM/yyyy" // Israeli Format
                                    placeholderText="בחר תאריך"
                                    className="w-full bg-transparent text-center text-lg font-bold text-slate-800 py-3 focus:outline-none cursor-pointer"
                                />
                            </div>
                        </div>

                        {/* Shift Types Grid */}
                        <div className="grid grid-cols-2 gap-3 mb-6">
                            {shiftTypes.map((shift) => {
                                const isSelected = selectedShiftCode === shift.code;
                                const config = shiftConfig[shift.code.toLowerCase()] || shiftConfig.middle;
                                const Icon = config.icon;
                                return (
                                    <button
                                        key={shift.code}
                                        onClick={() => handleShiftSelect(shift)}
                                        className={`flex flex-col items-center justify-center p-4 rounded-2xl border-2 transition-all h-24
                                            ${isSelected
                                            ? 'border-slate-800 bg-slate-800 text-white shadow-lg scale-[1.02]'
                                            : 'border-slate-100 bg-white text-slate-600 hover:border-slate-200 hover:bg-slate-50'}`}
                                    >
                                        <Icon className={`w-6 h-6 mb-2 ${isSelected ? 'text-white' : config.text}`} />
                                        <span className="font-bold text-sm">{shift.nameHe}</span>
                                    </button>
                                );
                            })}
                        </div>

                        {/* Time Inputs - Large Touch Targets */}
                        <div className="flex gap-4 mb-6">
                            <div className="flex-1">
                                <label className="block text-sm font-bold text-slate-700 mb-2 text-center">התחלה</label>
                                <input
                                    type="time"
                                    value={startTime}
                                    onChange={e => setStartTime(e.target.value)}
                                    className="w-full h-14 text-center text-xl font-bold bg-slate-50 rounded-2xl border border-slate-200 focus:border-slate-800 focus:ring-4 focus:ring-slate-100 outline-none transition-all"
                                />
                            </div>
                            <div className="flex-1">
                                <label className="block text-sm font-bold text-slate-700 mb-2 text-center">סיום</label>
                                <input
                                    type="time"
                                    value={endTime}
                                    onChange={e => setEndTime(e.target.value)}
                                    className="w-full h-14 text-center text-xl font-bold bg-slate-50 rounded-2xl border border-slate-200 focus:border-slate-800 focus:ring-4 focus:ring-slate-100 outline-none transition-all"
                                />
                            </div>
                        </div>

                        {/* Overtime (Collapsible) */}
                        <div className="mb-6">
                            <button onClick={() => setIsOvertimeOpen(!isOvertimeOpen)} className="text-sm font-bold text-indigo-600 flex items-center gap-1 py-2">
                                {isOvertimeOpen ? '- הסר תוספות' : '+ שעות נוספות / בונוס'}
                            </button>

                            {isOvertimeOpen && (
                                <div className="mt-2 bg-indigo-50/50 p-4 rounded-2xl border border-indigo-100 grid grid-cols-2 gap-4">
                                    <div>
                                        <label className="text-xs font-bold text-indigo-900 block mb-1">שעות נוספות</label>
                                        <input type="number" value={overtimeHours} onChange={e => setOvertimeHours(e.target.value)} className="w-full h-12 text-center rounded-xl border border-indigo-200 focus:ring-2 focus:ring-indigo-500/20 outline-none" placeholder="0" />
                                    </div>
                                    <div>
                                        <label className="text-xs font-bold text-indigo-900 block mb-1">אחוז (%)</label>
                                        <input type="number" value={overtimeRate} onChange={e => setOvertimeRate(e.target.value)} className="w-full h-12 text-center rounded-xl border border-indigo-200 focus:ring-2 focus:ring-indigo-500/20 outline-none" placeholder="150" />
                                    </div>
                                </div>
                            )}
                        </div>

                        {/* Save Button */}
                        <button
                            onClick={handleCreateShift}
                            disabled={!selectedShiftCode || !startTime || !endTime}
                            className="w-full h-14 bg-slate-900 text-white rounded-2xl font-bold text-lg shadow-xl shadow-slate-900/20 active:scale-95 transition-all disabled:opacity-50 disabled:shadow-none"
                        >
                            שמור
                        </button>
                    </div>
                </div>
            )}

            {/* Tip Modal */}
            {isTipOpen && (
                <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-sm flex items-center justify-center z-50 p-4">
                    <div className="bg-white rounded-[32px] w-full max-w-xs p-6 shadow-2xl animate-scale-in">
                        <h3 className="text-xl font-bold text-slate-800 text-center mb-6">הוספת טיפ</h3>
                        <div className="relative mb-6">
                            <span className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400 text-xl font-bold">₪</span>
                            <input type="number" value={tipAmount} onChange={(e) => setTipAmount(e.target.value)} className="w-full bg-slate-50 border-2 border-slate-100 rounded-2xl h-16 text-center text-4xl font-bold text-slate-900 focus:border-emerald-500 focus:ring-0 outline-none" autoFocus />
                        </div>
                        <div className="flex gap-3">
                            <button onClick={() => setIsTipOpen(false)} className="flex-1 py-3.5 rounded-2xl font-bold text-slate-500 bg-slate-50 hover:bg-slate-100">ביטול</button>
                            <button onClick={handleAddTip} className="flex-1 bg-emerald-500 text-white py-3.5 rounded-2xl font-bold shadow-lg shadow-emerald-500/30">אישור</button>
                        </div>
                    </div>
                </div>
            )}
        </Layout>
    );
}
