import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Layout from "../components/Layout";
import { useAuth } from "../context/AuthContext";
import api from "../api/client";

export default function SettingsPage() {
  const { user, refreshUser, logout } = useAuth();
  const navigate = useNavigate();
  const [hourlyRate, setHourlyRate] = useState(0);
  const [overtimeHourlyRate, setOvertimeHourlyRate] = useState(0);
  const [displayName, setDisplayName] = useState("");
  const [isSaving, setIsSaving] = useState(false);

  useEffect(() => {
    async function load() {
      try {
        const res = await api.get("/settings");
        setHourlyRate(res.data.hourlyRate ?? 0);
        setOvertimeHourlyRate(res.data.overtimeHourlyRate ?? 0);
        if (user) {
          setDisplayName(user.displayName || "");
        }
      } catch {
        // ignore
      }
    }
    load();
  }, [user]);

  async function handleSave() {
    setIsSaving(true);
    try {
      await api.post("/settings", {
        hourlyRate: Number(hourlyRate),
        overtimeHourlyRate: Number(overtimeHourlyRate),
      });
    } catch {
      // ignore for now
    } finally {
      setIsSaving(false);
    }
  }

  async function handleSaveName() {
    if (!displayName.trim()) return;
    setIsSaving(true);
    try {
      await api.post("/me/display-name", { displayName: displayName.trim() });
      await refreshUser();
    } catch {
      // ignore
    } finally {
      setIsSaving(false);
    }
  }

  async function handleLogout() {
    await logout();
  }

  return (
    <Layout>
      <header className="mb-6">
        <h1 className="text-xl font-semibold text-slate-900 text-right">
          הגדרות
        </h1>
        <p className="text-xs text-slate-500 text-right">
          נהל את פרטי השכר שלך
        </p>
      </header>

      <section className="space-y-4">
        <div className="rounded-3xl bg-white shadow-soft p-4 flex items-center justify-between gap-4">
          <div className="flex flex-col items-end gap-1">
            <span className="text-sm font-medium text-slate-800">
              שכר שעתי
            </span>
            <span className="text-[10px] text-slate-400">
              הזן את השכר לשעה
            </span>
          </div>
          <div className="flex items-center gap-2">
            <span className="text-xs text-slate-500">₪</span>
            <input
              type="number"
              value={hourlyRate}
              onChange={(e) => setHourlyRate(e.target.value)}
              className="w-20 rounded-xl border border-slate-200 px-2 py-1 text-sm text-left"
            />
          </div>
        </div>

        <button
          type="button"
          onClick={handleSave}
          className="w-full rounded-2xl bg-primary text-white py-3 text-sm font-semibold shadow-soft"
        >
          שמירה
        </button>
      </section>
    </Layout>
  );
}
