import { useEffect, useMemo, useState } from "react";
import { Sun, Sunset, Moon, Clock, X, Plus, Wallet, Pencil, Trash2 } from "lucide-react";
import Layout from "../components/Layout";
import StatCard from "../components/StatCard";
import { useAuth } from "../context/AuthContext";
import api from "../api/client";
import dayjs from "dayjs";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

function getGreeting(hour) {
    if (hour >= 5 && hour < 12) return "בוקר טוב";
    if (hour >= 12 && hour < 17) return "צהריים טובים";
    if (hour >= 17 && hour < 22) return "ערב טוב";
    return "לילה טוב";
}

export default function HomePage() {
    const { user, refreshUser } = useAuth();
    const [summary, setSummary] = useState(null);
    const [recentShifts, setRecentShifts] = useState([]);
    const [shiftTypes, setShiftTypes] = useState([]);
    const [isAddOpen, setIsAddOpen] = useState(false);
    const [isTipOpen, setIsTipOpen] = useState(false);
    const [selectedShiftCode, setSelectedShiftCode] = useState(null);
    const [selectedDate, setSelectedDate] = useState(new Date().toISOString().slice(0, 10));
    const [startTime, setStartTime] = useState("");
    const [endTime, setEndTime] = useState("");
    const [isOvertimeOpen, setIsOvertimeOpen] = useState(false);
    const [overtimeHours, setOvertimeHours] = useState("");
    const [overtimeRate, setOvertimeRate] = useState("");
    const [overtimeRateFromSettings, setOvertimeRateFromSettings] = useState(0);
    const [tipAmount, setTipAmount] = useState("");
    const [tipShiftId, setTipShiftId] = useState(null);
    const [editShiftId, setEditShiftId] = useState(null);
    const [hoveredShift, setHoveredShift] = useState(null);

    const shiftTypeMap = useMemo(() => {
        const map = {};
        shiftTypes.forEach(t => {
            map[t.nameHe] = t;
            map[t.code] = t;
        });
        return map;
    }, [shiftTypes]);

    const shiftConfig = {
        morning: { icon: Sun, color: 'text-amber-600', bg: 'bg-amber-50', border: 'border-amber-200' },
        evening: { icon: Sunset, color: 'text-orange-600', bg: 'bg-orange-50', border: 'border-orange-200' },
        night: { icon: Moon, color: 'text-indigo-600', bg: 'bg-indigo-50', border: 'border-indigo-200' },
        middle: { icon: Clock, color: 'text-teal-600', bg: 'bg-teal-50', border: 'border-teal-200' }
    };

    const userName = user?.displayName || "אורח";

    const greeting = useMemo(() => {
        const hour = new Date().getHours();
        return getGreeting(hour);
    }, []);

    async function refreshSummary() {
        try {
            const [summaryRes, historyRes] = await Promise.all([api.get("/summary"), api.get("/history")]);
            setSummary(summaryRes.data);
            if (historyRes.data.items) {
                const recent = historyRes.data.items.slice(0, 3);
                setRecentShifts(recent);
            }
        } catch {
            // ignore
        }
    }

    useEffect(() => {
        refreshUser();
        refreshSummary();
        api.get("/shift-types").then(res => setShiftTypes(res.data));
    }, []);

    const handleShiftSelect = (shift) => {
        setSelectedShiftCode(shift.code);
        setStartTime(shift.defaultStart);
        setEndTime(shift.defaultEnd);
    };

    const handleEditShift = (shift) => {
        setEditShiftId(shift.id);
        setSelectedDate(shift.date);
        const t = shiftTypeMap[shift.shiftType];
        if (t) setSelectedShiftCode(t.code);
        setStartTime(shift.startTime?.slice(0, 5) || "");
        setEndTime(shift.endTime?.slice(0, 5) || "");
        setOvertimeHours(shift.overtimeHours || "");
        setOvertimeRate(shift.overtimeHourlyRate || "");
        setIsOvertimeOpen(!!shift.overtimeHours);
        setIsAddOpen(true);
    };

    const handleOpenTip = (shiftId) => {
        setTipShiftId(shiftId);
        setTipAmount("");
        setIsTipOpen(true);
    };

    async function handleAddTip() {
        if (!tipShiftId || !tipAmount) return;
        try {
            await api.post(`/shifts/${tipShiftId}/tip`, {
                tipAmount: Number(tipAmount)
            });
            setIsTipOpen(false);
            setTipShiftId(null);
            setTipAmount("");
            refreshSummary();
            refreshUser();
        } catch (error) {
            console.error("Error adding tip:", error);
        }
    }

    async function handleCreateShift() {
        if (!selectedShiftCode || !selectedDate) return;
        try {
            const payload = {
                shiftCode: selectedShiftCode,
                date: selectedDate,
                startTime,
                endTime,
                overtimeHours: overtimeHours ? Number(overtimeHours) : 0,
                overtimeHourlyRate: overtimeRate
                    ? Number(overtimeRate)
                    : (overtimeHours ? overtimeRateFromSettings : null),
            };

            if (editShiftId) {
                await api.put(`/shifts/${editShiftId}`, payload);
            } else {
                await api.post("/shifts", payload);
            }

            closeAddModal();
            refreshSummary();
            refreshUser();
        } catch {
            alert("שגיאה בשמירה");
        }
    }

    const closeAddModal = () => {
        setIsAddOpen(false);
        setEditShiftId(null);
        setSelectedShiftCode(null);
        setStartTime("");
        setEndTime("");
        setIsOvertimeOpen(false);
        setOvertimeHours("");
        setOvertimeRate("");
        setSelectedDate(new Date().toISOString().slice(0, 10));
    };

    return (
        <Layout>
            <header className="mb-6">
                <h1 className="text-lg font-medium text-zinc-900 mb-0.5">
                    {greeting}, {userName}
                </h1>
                <p className="text-xs text-zinc-500">
                    {new Date().toLocaleDateString("he-IL", {
                        weekday: "long", month: "long", day: "numeric"
                    })}
                </p>
            </header>

            {/* Stats */}
            <section className="grid grid-cols-3 gap-3 mb-6">
                <div className="bg-white rounded-xl border border-zinc-200/60 p-4">
                    <div className="text-xs text-zinc-500 mb-1">שעות השבוע</div>
                    <div className="text-2xl font-semibold text-zinc-900">
                        {(summary?.weekHours ?? 0).toFixed(1)}
                    </div>
                </div>
                <div className="bg-white rounded-xl border border-zinc-200/60 p-4">
                    <div className="text-xs text-zinc-500 mb-1">שעות החודש</div>
                    <div className="text-2xl font-semibold text-zinc-900">
                        {(summary?.monthHours ?? 0).toFixed(1)}
                    </div>
                </div>
                <div className="bg-white rounded-xl border border-zinc-200/60 p-4">
                    <div className="text-xs text-zinc-500 mb-1">משכורת</div>
                    <div className="text-xl font-semibold text-zinc-900">
                        ₪{(summary?.expectedMonthSalary ?? 0).toFixed(0)}
                    </div>
                </div>
            </section>

            {/* Recent Shifts */}
            <section>
                <h2 className="text-sm font-medium text-zinc-700 mb-3">משמרות אחרונות</h2>
                <div className="space-y-2">
                    {recentShifts.map((item) => {
                        const now = dayjs();
                        let ongoing = false;

                        if (!item.isEnded) {
                            const formatTime = (t) => Array.isArray(t) ? `${String(t[0]).padStart(2, '0')}:${String(t[1]).padStart(2, '0')}` : (t?.slice(0, 5) || "");
                            const formatDate = (d) => Array.isArray(d) ? `${d[0]}-${String(d[1]).padStart(2, '0')}-${String(d[2]).padStart(2, '0')}` : d;

                            const dateStr = formatDate(item.date);
                            const sTime = formatTime(item.startTime) || shiftTypeMap[item.shiftType]?.defaultStart;
                            const eTime = formatTime(item.endTime) || shiftTypeMap[item.shiftType]?.defaultEnd;

                            if (dateStr && sTime && eTime) {
                                const start = dayjs(`${dateStr}T${sTime}`);
                                let end = dayjs(`${dateStr}T${eTime}`);
                                if (end.isBefore(start)) end = end.add(1, "day");
                                ongoing = now.isAfter(start) && now.add(5, 'minute').isBefore(end);
                            }
                        }

                        return (
                            <div
                                key={item.id}
                                onMouseEnter={() => setHoveredShift(item.id)}
                                onMouseLeave={() => setHoveredShift(null)}
                                className={`bg-white rounded-xl border p-4 transition-all ${
                                    ongoing ? 'border-emerald-300 bg-emerald-50/30' : 'border-zinc-200/60 hover:border-zinc-300'
                                }`}
                            >
                                <div className="flex items-start justify-between mb-3">
                                    <div className="flex items-center gap-2">
                                        <div className="text-sm font-medium text-zinc-900">
                                            {item.shiftType || item.name}
                                        </div>
                                        {ongoing && (
                                            <span className="px-2 py-0.5 rounded-md bg-emerald-100 text-emerald-700 text-[10px] font-semibold">
                                                פעיל
                                            </span>
                                        )}
                                    </div>
                                    <div className="text-lg font-semibold text-zinc-900">
                                        ₪{item.salary.toFixed(0)}
                                    </div>
                                </div>

                                <div className="flex items-center justify-between text-xs text-zinc-500 mb-3">
                                    <span>
                                        {new Date(item.date).toLocaleDateString("he-IL", { weekday: "short", day: "numeric", month: "short" })}
                                    </span>
                                    <span>{item.hours.toFixed(1)} שעות</span>
                                </div>

                                {(hoveredShift === item.id || ongoing) && (
                                    <div className="flex items-center gap-2 pt-3 border-t border-zinc-100">
                                        {ongoing && (
                                            <button
                                                onClick={async (e) => {
                                                    e.stopPropagation();
                                                    if (window.confirm("האם לסיים את המשמרת עכשיו?")) {
                                                        try {
                                                            const nowStr = dayjs().format("HH:mm");
                                                            setRecentShifts(prev => prev.map(s =>
                                                                s.id === item.id ? { ...s, endTime: nowStr, isEnded: true } : s
                                                            ));
                                                            await api.post(`/shifts/${item.id}/end`);
                                                            refreshSummary();
                                                            refreshUser();
                                                        } catch (err) {
                                                            console.error("Failed to end shift", err);
                                                            refreshSummary();
                                                        }
                                                    }
                                                }}
                                                className="px-3 py-1.5 rounded-lg bg-emerald-600 text-white text-xs font-medium hover:bg-emerald-700 transition-colors"
                                            >
                                                סיים משמרת
                                            </button>
                                        )}
                                        <button
                                            onClick={() => handleOpenTip(item.id)}
                                            className="flex-1 px-3 py-1.5 rounded-lg bg-zinc-900 text-white text-xs font-medium hover:bg-zinc-800 transition-colors flex items-center justify-center gap-1.5"
                                        >
                                            <Wallet className="w-3.5 h-3.5" />
                                            טיפ
                                        </button>
                                        <button
                                            onClick={(e) => {
                                                e.stopPropagation();
                                                handleEditShift(item);
                                            }}
                                            className="p-1.5 rounded-lg bg-zinc-100 text-zinc-600 hover:bg-zinc-200 transition-colors"
                                        >
                                            <Pencil className="w-4 h-4" />
                                        </button>
                                        <button
                                            onClick={async (e) => {
                                                e.stopPropagation();
                                                if (window.confirm("האם למחוק את המשמרת?")) {
                                                    try {
                                                        await api.delete(`/shifts/${item.id}`);
                                                        refreshSummary();
                                                        refreshUser();
                                                    } catch (err) {
                                                        console.error("Failed to delete", err);
                                                        alert("שגיאה במחיקה");
                                                    }
                                                }
                                            }}
                                            className="p-1.5 rounded-lg bg-red-50 text-red-600 hover:bg-red-100 transition-colors"
                                        >
                                            <Trash2 className="w-4 h-4" />
                                        </button>
                                    </div>
                                )}
                            </div>
                        );
                    })}
                    {recentShifts.length === 0 && (
                        <p className="text-xs text-zinc-400 text-center py-8">
                            אין משמרות שמורות
                        </p>
                    )}
                </div>
            </section>

            {/* Floating Action Button */}
            <button
                onClick={() => setIsAddOpen(true)}
                className="fixed bottom-20 left-1/2 -translate-x-1/2 bg-zinc-900 text-white px-6 py-3 rounded-xl shadow-lg flex items-center gap-2 font-medium z-40 hover:bg-zinc-800 transition-all"
            >
                <Plus className="w-5 h-5" />
                הוסף משמרת
            </button>

            {/* Add Shift Modal */}
            {isAddOpen && (
                <div className="fixed inset-0 bg-black/40 backdrop-blur-sm flex items-end justify-center z-50">
                    <div className="bg-white rounded-t-2xl max-w-md w-full px-6 pt-6 pb-8 max-h-[90vh] overflow-y-auto">
                        <div className="flex items-center justify-between mb-6">
                            <h2 className="text-lg font-medium text-zinc-900">
                                {editShiftId ? 'עריכת משמרת' : 'משמרת חדשה'}
                            </h2>
                            <button onClick={closeAddModal} className="text-zinc-400 hover:text-zinc-600">
                                <X className="w-5 h-5" />
                            </button>
                        </div>

                        {/* Date */}
                        <div className="mb-5">
                            <label className="block text-xs font-medium text-zinc-600 mb-2">תאריך</label>
                            <DatePicker
                                selected={selectedDate ? new Date(selectedDate) : null}
                                onChange={(date) => {
                                    if (date) {
                                        const offsetDate = new Date(date.getTime() - (date.getTimezoneOffset() * 60000));
                                        setSelectedDate(offsetDate.toISOString().slice(0, 10));
                                    } else {
                                        setSelectedDate("");
                                    }
                                }}
                                dateFormat="dd-MM-yyyy"
                                className="w-full rounded-xl border border-zinc-200 px-4 py-2.5 text-sm focus:outline-none focus:ring-2 focus:ring-zinc-900/10 focus:border-zinc-400"
                                placeholderText="בחר תאריך"
                                wrapperClassName="w-full"
                            />
                        </div>

                        {/* Shift Types */}
                        <div className="mb-5">
                            <label className="block text-xs font-medium text-zinc-600 mb-2">סוג משמרת</label>
                            <div className="grid grid-cols-2 gap-2">
                                {shiftTypes.map((shift) => {
                                    const config = shiftConfig[shift.code?.toLowerCase()] || {
                                        icon: Clock, color: 'text-zinc-600', bg: 'bg-zinc-50', border: 'border-zinc-200'
                                    };
                                    const Icon = config.icon;
                                    const isSelected = selectedShiftCode === shift.code;

                                    return (
                                        <button
                                            key={shift.code}
                                            type="button"
                                            onClick={() => handleShiftSelect(shift)}
                                            className={`flex items-center gap-2 rounded-xl p-3 border transition-all ${
                                                isSelected
                                                    ? `${config.bg} ${config.border} ${config.color}`
                                                    : 'bg-white border-zinc-200 text-zinc-600 hover:border-zinc-300'
                                            }`}
                                        >
                                            <Icon className="w-4 h-4" />
                                            <span className="text-sm font-medium">{shift.nameHe}</span>
                                        </button>
                                    );
                                })}
                            </div>
                        </div>

                        {/* Times */}
                        <div className="grid grid-cols-2 gap-3 mb-5">
                            <div>
                                <label className="block text-xs font-medium text-zinc-600 mb-2">התחלה</label>
                                <input
                                    type="time"
                                    value={startTime}
                                    onChange={(e) => setStartTime(e.target.value)}
                                    className="w-full rounded-xl border border-zinc-200 px-3 py-2.5 text-sm focus:outline-none focus:ring-2 focus:ring-zinc-900/10 focus:border-zinc-400"
                                />
                            </div>
                            <div>
                                <label className="block text-xs font-medium text-zinc-600 mb-2">סיום</label>
                                <input
                                    type="time"
                                    value={endTime}
                                    onChange={(e) => setEndTime(e.target.value)}
                                    className="w-full rounded-xl border border-zinc-200 px-3 py-2.5 text-sm focus:outline-none focus:ring-2 focus:ring-zinc-900/10 focus:border-zinc-400"
                                />
                            </div>
                        </div>

                        {/* Overtime */}
                        {!isOvertimeOpen && (
                            <button
                                type="button"
                                onClick={() => setIsOvertimeOpen(true)}
                                className="w-full mb-5 rounded-xl border border-dashed border-zinc-300 bg-zinc-50/50 text-zinc-600 py-2.5 text-xs font-medium hover:border-zinc-400 hover:bg-zinc-100/50 transition-all"
                            >
                                + שעות נוספות
                            </button>
                        )}

                        {isOvertimeOpen && (
                            <div className="mb-5 p-4 rounded-xl bg-amber-50 border border-amber-200">
                                <div className="flex items-center justify-between mb-3">
                                    <span className="text-xs font-medium text-amber-900">שעות נוספות</span>
                                    <button
                                        type="button"
                                        onClick={() => {
                                            setIsOvertimeOpen(false);
                                            setOvertimeHours('');
                                            setOvertimeRate('');
                                        }}
                                        className="text-xs text-amber-700 hover:text-amber-900"
                                    >
                                        הסר
                                    </button>
                                </div>
                                <div className="grid grid-cols-2 gap-3">
                                    <div>
                                        <label className="block text-xs text-amber-800 mb-1.5">שעות</label>
                                        <input
                                            type="number"
                                            min="0"
                                            step="0.25"
                                            value={overtimeHours}
                                            onChange={(e) => setOvertimeHours(e.target.value)}
                                            className="w-full rounded-lg border border-amber-200 bg-white px-3 py-2 text-sm focus:outline-none focus:border-amber-400"
                                            placeholder="0"
                                        />
                                    </div>
                                    <div>
                                        <label className="block text-xs text-amber-800 mb-1.5">תעריף (₪)</label>
                                        <input
                                            type="number"
                                            min="0"
                                            value={overtimeRate || overtimeRateFromSettings}
                                            onChange={(e) => setOvertimeRate(e.target.value)}
                                            className="w-full rounded-lg border border-amber-200 bg-white px-3 py-2 text-sm focus:outline-none focus:border-amber-400"
                                            placeholder={overtimeRateFromSettings || '0'}
                                        />
                                    </div>
                                </div>
                            </div>
                        )}

                        <button
                            type="button"
                            onClick={handleCreateShift}
                            className="w-full rounded-xl bg-zinc-900 text-white py-3 text-sm font-medium disabled:opacity-50 hover:bg-zinc-800 transition-all"
                            disabled={!selectedShiftCode || !startTime || !endTime}
                        >
                            {editShiftId ? 'עדכן משמרת' : 'שמור משמרת'}
                        </button>
                    </div>
                </div>
            )}

            {/* Tip Modal */}
            {isTipOpen && (
                <div className="fixed inset-0 bg-black/40 backdrop-blur-sm flex items-center justify-center z-50 p-4">
                    <div className="bg-white rounded-2xl max-w-sm w-full px-6 py-6">
                        <div className="flex items-center justify-between mb-5">
                            <h2 className="text-lg font-medium text-zinc-900">הוסף טיפ</h2>
                            <button onClick={() => setIsTipOpen(false)} className="text-zinc-400 hover:text-zinc-600">
                                <X className="w-5 h-5" />
                            </button>
                        </div>

                        <div className="mb-5">
                            <label className="block text-xs font-medium text-zinc-600 mb-2">סכום (₪)</label>
                            <input
                                type="number"
                                min="0"
                                value={tipAmount}
                                onChange={(e) => setTipAmount(e.target.value)}
                                className="w-full rounded-xl border border-zinc-200 px-4 py-3 text-lg text-center focus:outline-none focus:ring-2 focus:ring-zinc-900/10 focus:border-zinc-400"
                                placeholder="0"
                                autoFocus
                            />
                        </div>

                        <button
                            type="button"
                            onClick={handleAddTip}
                            className="w-full rounded-xl bg-zinc-900 text-white py-3 text-sm font-medium disabled:opacity-50 hover:bg-zinc-800 transition-all"
                            disabled={!tipAmount || Number(tipAmount) <= 0}
                        >
                            שמור טיפ
                        </button>
                    </div>
                </div>
            )}
        </Layout>
    );
}
