import { useEffect, useMemo, useState } from "react";
import { Sun, Sunset, Moon, Clock, X, Plus, Wallet, Pencil, Trash2, MoreVertical } from "lucide-react";
import Layout from "../components/Layout";
import { useAuth } from "../context/AuthContext";
import api from "../api/client";
import dayjs from "dayjs";

// --- Localization Imports ---
import DatePicker, { registerLocale } from "react-datepicker";
import he from "date-fns/locale/he"; // Import Hebrew locale
import "react-datepicker/dist/react-datepicker.css";
registerLocale('he', he); // Register it

function getGreeting(hour) {
    if (hour >= 5 && hour < 12) return "בוקר טוב";
    if (hour >= 12 && hour < 17) return "צהריים טובים";
    if (hour >= 17 && hour < 22) return "ערב טוב";
    return "לילה טוב";
}

export default function HomePage() {
    const { user, refreshUser } = useAuth();

    // Data States
    const [summary, setSummary] = useState(null);
    const [recentShifts, setRecentShifts] = useState([]);
    const [shiftTypes, setShiftTypes] = useState([]);

    // UI States
    const [isAddOpen, setIsAddOpen] = useState(false);
    const [isTipOpen, setIsTipOpen] = useState(false);
    const [activeMenuId, setActiveMenuId] = useState(null);
    const [endedLocalIds, setEndedLocalIds] = useState([]);

    // Form States
    const [selectedShiftCode, setSelectedShiftCode] = useState(null);
    const [selectedDate, setSelectedDate] = useState(new Date().toISOString().slice(0, 10));
    const [startTime, setStartTime] = useState("");
    const [endTime, setEndTime] = useState("");

    // Overtime & Tips
    const [isOvertimeOpen, setIsOvertimeOpen] = useState(false);
    const [overtimeHours, setOvertimeHours] = useState("");
    const [overtimeRate, setOvertimeRate] = useState("");
    const [overtimeRateFromSettings, setOvertimeRateFromSettings] = useState(0);
    const [tipAmount, setTipAmount] = useState("");
    const [tipShiftId, setTipShiftId] = useState(null);
    const [editShiftId, setEditShiftId] = useState(null);

    // --- Click Outside to Close Menus ---
    useEffect(() => {
        const handleClickOutside = () => setActiveMenuId(null);
        window.addEventListener('click', handleClickOutside);
        return () => window.removeEventListener('click', handleClickOutside);
    }, []);

    // --- Data Loading ---
    useEffect(() => {
        refreshUser();
        refreshSummary();
        api.get("/shift-types").then(res => setShiftTypes(res.data));
    }, []);

    async function refreshSummary() {
        try {
            const [summaryRes, historyRes, settingsRes] = await Promise.all([
                api.get("/summary"),
                api.get("/history"),
                api.get("/settings")
            ]);
            setSummary(summaryRes.data);
            if (historyRes.data.items) setRecentShifts(historyRes.data.items.slice(0, 5)); // Show 5 items on mobile
            if (settingsRes.data.overtimeHourlyRate) setOvertimeRateFromSettings(settingsRes.data.overtimeHourlyRate);
        } catch (e) { console.error(e); }
    }

    // --- Helpers ---
    const shiftTypeMap = useMemo(() => {
        const map = {};
        shiftTypes.forEach(t => { map[t.nameHe] = t; map[t.code] = t; });
        return map;
    }, [shiftTypes]);

    const shiftConfig = {
        morning: { icon: Sun, bg: 'bg-orange-100', text: 'text-orange-600', border: 'border-orange-100' },
        evening: { icon: Sunset, bg: 'bg-blue-100', text: 'text-blue-600', border: 'border-blue-100' },
        night: { icon: Moon, bg: 'bg-indigo-100', text: 'text-indigo-600', border: 'border-indigo-100' },
        middle: { icon: Clock, bg: 'bg-teal-100', text: 'text-teal-600', border: 'border-teal-100' }
    };

    // --- Handlers ---
    const handleShiftSelect = (shift) => {
        setSelectedShiftCode(shift.code);
        setStartTime(shift.defaultStart);
        setEndTime(shift.defaultEnd);
    };

    const handleEditShift = (shift) => {
        setEditShiftId(shift.id);
        setSelectedDate(shift.date);
        const t = shiftTypeMap[shift.shiftType];
        if (t) setSelectedShiftCode(t.code);
        setStartTime(shift.startTime?.slice(0, 5) || "");
        setEndTime(shift.endTime?.slice(0, 5) || "");
        setOvertimeHours(shift.overtimeHours || "");
        setOvertimeRate(shift.overtimeHourlyRate || "");
        setIsOvertimeOpen(!!shift.overtimeHours);
        setIsAddOpen(true);
        setActiveMenuId(null);
    };

    const handleCreateShift = async () => {
        if (!selectedShiftCode || !selectedDate) return;
        try {
            const payload = {
                shiftCode: selectedShiftCode,
                date: selectedDate,
                startTime,
                endTime,
                overtimeHours: overtimeHours ? Number(overtimeHours) : 0,
                overtimeHourlyRate: overtimeRate ? Number(overtimeRate) : (overtimeHours ? overtimeRateFromSettings : null),
            };

            if (editShiftId) await api.put(`/shifts/${editShiftId}`, payload);
            else await api.post("/shifts", payload);

            closeAddModal();
            refreshSummary();
            refreshUser();
        } catch { alert("שגיאה בשמירה"); }
    };

    const handleAddTip = async () => {
        if (!tipShiftId || !tipAmount) return;
        await api.post(`/shifts/${tipShiftId}/tip`, { tipAmount: Number(tipAmount) });
        setIsTipOpen(false); setTipShiftId(null); setTipAmount("");
        refreshSummary();
    };

    const closeAddModal = () => {
        setIsAddOpen(false);
        setEditShiftId(null);
        setSelectedShiftCode(null);
        setStartTime(""); setEndTime("");
        setIsOvertimeOpen(false);
        setOvertimeHours(""); setOvertimeRate("");
        setSelectedDate(new Date().toISOString().slice(0, 10));
    };

    return (
        <Layout>
            {/* Header */}
            <header className="mb-6 px-1 pt-2" dir="rtl">
                <h1 className="text-2xl font-bold text-slate-900">
                    {getGreeting(new Date().getHours())}, {user?.displayName || "אורח"}
                </h1>
                <p className="text-sm text-slate-500 font-medium">
                    {new Date().toLocaleDateString("he-IL", { weekday: "long", day: "numeric", month: "long" })}
                </p>
            </header>

            {/* Stats Cards */}
            <section className="mb-8" dir="rtl">
                <div className="grid grid-cols-2 gap-3">
                    <div className="col-span-2 bg-slate-900 rounded-[2rem] p-6 text-white shadow-xl shadow-slate-200">
                        <div className="text-sm text-slate-400 font-medium mb-1">משכורת צפויה</div>
                        <div className="text-4xl font-bold tracking-tight">
                            ₪{(summary?.expectedMonthSalary ?? 0).toFixed(0)}
                        </div>
                    </div>
                    <div className="bg-white rounded-3xl p-5 border border-slate-100 shadow-sm">
                        <div className="text-xs font-bold text-slate-400 mb-2">שעות החודש</div>
                        <div className="text-2xl font-bold text-slate-800">
                            {(summary?.monthHours ?? 0).toFixed(1)}
                        </div>
                    </div>
                    <div className="bg-white rounded-3xl p-5 border border-slate-100 shadow-sm">
                        <div className="text-xs font-bold text-slate-400 mb-2">טיפים</div>
                        <div className="text-2xl font-bold text-emerald-600">
                            ₪{(summary?.totalTips ?? 0).toFixed(0)}
                        </div>
                    </div>
                </div>
            </section>

            {/* Recent Shifts List */}
            <section dir="rtl" className="pb-24">
                <h2 className="text-lg font-bold text-slate-800 mb-4 px-1">משמרות אחרונות</h2>
                <div className="space-y-3">
                    {recentShifts.map((item) => {
                        const config = shiftConfig[item.shiftType?.toLowerCase()] || shiftConfig.middle;
                        const Icon = config.icon;

                        // Localization Logic
                        const d = new Date(item.date);
                        const dayName = d.toLocaleDateString('he-IL', { weekday: 'long' });
                        const dateNum = d.toLocaleDateString('he-IL', { day: '2-digit', month: '2-digit' });

                        return (
                            <div key={item.id} className="bg-white rounded-2xl p-1 pr-1 border border-slate-100 shadow-sm flex items-center gap-3">

                                {/* Date Block (Left Side visually, Right in Code due to RTL) */}
                                <div className="bg-slate-50 rounded-xl h-20 w-16 flex flex-col items-center justify-center shrink-0 border border-slate-100">
                                    <span className="text-[10px] font-bold text-slate-400 leading-tight">{dayName}</span>
                                    <span className="text-lg font-bold text-slate-800 leading-tight">{dateNum}</span>
                                </div>

                                {/* Main Info */}
                                <div className="flex-1 min-w-0 py-3">
                                    <div className="flex items-center gap-2 mb-1">
                                        <div className={`p-1.5 rounded-lg ${config.bg} ${config.text}`}>
                                            <Icon className="w-4 h-4" />
                                        </div>
                                        <h3 className="text-base font-bold text-slate-900 truncate">
                                            {item.shiftType || "משמרת"}
                                        </h3>
                                    </div>
                                    <div className="text-sm text-slate-500 font-medium">
                                        {item.startTime?.slice(0, 5)} - {item.endTime?.slice(0, 5)}
                                    </div>
                                </div>

                                {/* Salary & Menu */}
                                <div className="pl-4 flex flex-col items-end gap-1">
                                    <span className="text-base font-bold text-slate-900">₪{item.salary.toFixed(0)}</span>
                                    {/* Action Buttons */}
                                    <div className="flex items-center">
                                        <button
                                            onClick={(e) => { e.stopPropagation(); setTipShiftId(item.id); setTipAmount(item.tip || ""); setIsTipOpen(true); }}
                                            className={`text-xs font-bold px-2 py-1 rounded-lg ${item.tip > 0 ? 'bg-emerald-100 text-emerald-700' : 'bg-slate-100 text-slate-400'}`}
                                        >
                                            {item.tip > 0 ? `+${item.tip}` : '+טיפ'}
                                        </button>
                                        <div className="relative mr-1">
                                            <button onClick={(e) => { e.stopPropagation(); setActiveMenuId(activeMenuId === item.id ? null : item.id); }}>
                                                <MoreVertical className="w-5 h-5 text-slate-300" />
                                            </button>
                                            {/* Dropdown Menu */}
                                            {activeMenuId === item.id && (
                                                <div className="absolute left-0 top-6 w-32 bg-white rounded-xl shadow-xl border border-slate-100 z-20 overflow-hidden">
                                                    <button onClick={(e)=>{e.stopPropagation(); handleEditShift(item)}} className="w-full p-3 text-xs font-bold text-slate-600 hover:bg-slate-50 text-right">ערוך</button>
                                                    {/*<button onClick={async (e)=>{e.stopPropagation(); setActiveMenuId(null); if(confirm('למחוק?')) { await api.delete(`/shifts/${item.id}`); refreshSummary(); }}} className="w-full p-3 text-xs font-bold text-red-500 hover:bg-red-50 text-right">מחק</button>*/}
                                                </div>
                                            )}
                                        </div>
                                    </div>
                                </div>
                            </div>
                        );
                    })}
                </div>
            </section>

            {/* FAB - Bottom Right (Ergonomic) */}
            <button
                onClick={() => setIsAddOpen(true)}
                className="fixed bottom-6 left-6 bg-slate-900 text-white w-14 h-14 rounded-full shadow-2xl shadow-slate-900/40 flex items-center justify-center hover:scale-105 active:scale-95 transition-all z-40"
            >
                <Plus className="w-7 h-7" />
            </button>

            {/* --- ADD SHIFT MODAL (Bottom Sheet) --- */}
            {isAddOpen && (
                <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-sm z-50 flex items-end justify-center">
                    {/* The Sheet */}
                    <div className="bg-white w-full max-w-lg rounded-t-[32px] p-6 max-h-[90vh] overflow-y-auto animate-slide-up" dir="rtl">

                        <div className="flex items-center justify-between mb-6">
                            <h2 className="text-xl font-bold text-slate-900">
                                {editShiftId ? 'עריכת משמרת' : 'הוספת משמרת'}
                            </h2>
                            <button onClick={closeAddModal} className="p-2 bg-slate-100 rounded-full text-slate-500">
                                <X className="w-5 h-5" />
                            </button>
                        </div>

                        {/* Date - Big & Bold */}
                        <div className="mb-6">
                            <label className="text-sm font-bold text-slate-700 mb-2 block">תאריך</label>
                            <div className="bg-slate-50 rounded-2xl p-1 border border-slate-200">
                                <DatePicker
                                    selected={selectedDate ? new Date(selectedDate) : null}
                                    onChange={(date) => {
                                        if (date) {
                                            const offsetDate = new Date(date.getTime() - (date.getTimezoneOffset() * 60000));
                                            setSelectedDate(offsetDate.toISOString().slice(0, 10));
                                        }
                                    }}
                                    locale="he" // Force Hebrew
                                    dateFormat="dd/MM/yyyy"
                                    className="w-full bg-transparent text-center text-lg font-bold text-slate-800 py-3 focus:outline-none cursor-pointer"
                                />
                            </div>
                        </div>

                        {/* Shift Types - Big Buttons */}
                        <div className="grid grid-cols-2 gap-3 mb-6">
                            {shiftTypes.map((shift) => {
                                const isSelected = selectedShiftCode === shift.code;
                                const config = shiftConfig[shift.code.toLowerCase()] || shiftConfig.middle;
                                const Icon = config.icon;
                                return (
                                    <button
                                        key={shift.code}
                                        onClick={() => handleShiftSelect(shift)}
                                        className={`flex flex-col items-center justify-center h-24 rounded-2xl border-2 transition-all ${
                                            isSelected
                                                ? 'bg-slate-800 border-slate-800 text-white shadow-lg scale-[1.02]'
                                                : 'bg-white border-slate-100 text-slate-500 hover:border-slate-200'
                                        }`}
                                    >
                                        <Icon className={`w-6 h-6 mb-2 ${isSelected ? 'text-white' : config.text}`} />
                                        <span className="font-bold text-sm">{shift.nameHe}</span>
                                    </button>
                                );
                            })}
                        </div>

                        {/* Times - Large Inputs */}
                        <div className="flex gap-4 mb-6">
                            <div className="flex-1">
                                <label className="text-sm font-bold text-slate-700 mb-2 block text-center">התחלה</label>
                                <input
                                    type="time"
                                    value={startTime}
                                    onChange={(e) => setStartTime(e.target.value)}
                                    className="w-full h-14 bg-slate-50 border border-slate-200 rounded-2xl text-center text-xl font-bold text-slate-900 focus:ring-2 focus:ring-slate-900 focus:border-transparent outline-none"
                                />
                            </div>
                            <div className="flex-1">
                                <label className="text-sm font-bold text-slate-700 mb-2 block text-center">סיום</label>
                                <input
                                    type="time"
                                    value={endTime}
                                    onChange={(e) => setEndTime(e.target.value)}
                                    className="w-full h-14 bg-slate-50 border border-slate-200 rounded-2xl text-center text-xl font-bold text-slate-900 focus:ring-2 focus:ring-slate-900 focus:border-transparent outline-none"
                                />
                            </div>
                        </div>

                        {/* Overtime Toggle */}
                        <div className="mb-6">
                            <button
                                onClick={() => setIsOvertimeOpen(!isOvertimeOpen)}
                                className="text-sm font-bold text-indigo-600 flex items-center gap-2"
                            >
                                {isOvertimeOpen ? '- הסר תוספות' : '+ הוסף שעות נוספות / בונוס'}
                            </button>
                            {isOvertimeOpen && (
                                <div className="mt-3 p-4 bg-indigo-50/50 border border-indigo-100 rounded-2xl grid grid-cols-2 gap-4 animate-fade-in">
                                    <div>
                                        <label className="text-xs font-bold text-indigo-900 block mb-1">שעות נוספות</label>
                                        <input type="number" value={overtimeHours} onChange={e => setOvertimeHours(e.target.value)} className="w-full h-12 text-center rounded-xl border border-indigo-200" placeholder="0" />
                                    </div>
                                    <div>
                                        <label className="text-xs font-bold text-indigo-900 block mb-1">אחוז (%)</label>
                                        <input type="number" value={overtimeRate} onChange={e => setOvertimeRate(e.target.value)} className="w-full h-12 text-center rounded-xl border border-indigo-200" placeholder="150" />
                                    </div>
                                </div>
                            )}
                        </div>

                        <button
                            onClick={handleCreateShift}
                            disabled={!selectedShiftCode || !startTime || !endTime}
                            className="w-full h-14 bg-slate-900 text-white rounded-2xl font-bold text-lg shadow-xl shadow-slate-900/20 active:scale-95 transition-all disabled:opacity-50"
                        >
                            שמור שינויים
                        </button>
                    </div>
                </div>
            )}

            {/* Tip Modal - Centered but Large */}
            {isTipOpen && (
                <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-sm flex items-center justify-center z-50 p-6">
                    <div className="bg-white rounded-[32px] w-full max-w-xs p-6 shadow-2xl" dir="rtl">
                        <h3 className="text-xl font-bold text-slate-900 text-center mb-6">הוספת טיפ</h3>
                        <div className="relative mb-6">
                            <input
                                type="number"
                                value={tipAmount}
                                onChange={(e) => setTipAmount(e.target.value)}
                                className="w-full h-20 bg-slate-50 rounded-2xl text-center text-4xl font-bold text-slate-900 border-2 border-slate-100 focus:border-emerald-500 outline-none"
                                placeholder="0"
                                autoFocus
                            />
                        </div>
                        <div className="flex gap-3">
                            <button onClick={() => setIsTipOpen(false)} className="flex-1 py-4 rounded-2xl font-bold text-slate-500 bg-slate-50">ביטול</button>
                            <button onClick={handleAddTip} className="flex-1 bg-emerald-500 text-white py-4 rounded-2xl font-bold shadow-lg shadow-emerald-500/30">אישור</button>
                        </div>
                    </div>
                </div>
            )}
        </Layout>
    );
}
