import { useEffect, useMemo, useState } from "react";
import { Sun, Sunset, Moon, Clock, X, Plus, Wallet, Pencil, Trash2, MoreVertical, ChevronRight, Coins } from "lucide-react";
import Layout from "../components/Layout";
import StatCard from "../components/StatCard";
import { useAuth } from "../context/AuthContext";
import api from "../api/client";
import dayjs from "dayjs";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

function getGreeting(hour) {
    if (hour >= 5 && hour < 12) return "בוקר טוב";
    if (hour >= 12 && hour < 17) return "צהריים טובים";
    if (hour >= 17 && hour < 22) return "ערב טוב";
    return "לילה טוב";
}

export default function HomePage() {
    const { user, refreshUser } = useAuth();
    const [summary, setSummary] = useState(null);
    const [recentShifts, setRecentShifts] = useState([]);
    const [shiftTypes, setShiftTypes] = useState([]);
    const [isAddOpen, setIsAddOpen] = useState(false);
    const [isTipOpen, setIsTipOpen] = useState(false);
    const [selectedShiftCode, setSelectedShiftCode] = useState(null);
    const [selectedDate, setSelectedDate] = useState(new Date().toISOString().slice(0, 10));
    const [startTime, setStartTime] = useState("");
    const [endTime, setEndTime] = useState("");
    const [isOvertimeOpen, setIsOvertimeOpen] = useState(false);
    const [overtimeHours, setOvertimeHours] = useState("");
    const [overtimeRate, setOvertimeRate] = useState("");
    const [overtimeRateFromSettings, setOvertimeRateFromSettings] = useState(0);
    const [tipAmount, setTipAmount] = useState("");
    const [tipShiftId, setTipShiftId] = useState(null);
    const [editShiftId, setEditShiftId] = useState(null);
    const [endedLocalIds, setEndedLocalIds] = useState([]);
    const [activeMenuId, setActiveMenuId] = useState(null);

    // Close menu when clicking outside
    useEffect(() => {
        const handleClickOutside = () => setActiveMenuId(null);
        window.addEventListener('click', handleClickOutside);
        return () => window.removeEventListener('click', handleClickOutside);
    }, []);

    const shiftTypeMap = useMemo(() => {
        const map = {};
        shiftTypes.forEach(t => {
            map[t.nameHe] = t;
            map[t.code] = t;
        });
        return map;
    }, [shiftTypes]);

    // Updated colors to be softer and more modern
    const shiftConfig = {
        morning: {
            icon: Sun,
            bg: 'bg-orange-50',
            text: 'text-orange-500',
            border: 'border-orange-100'
        },
        evening: {
            icon: Sunset,
            bg: 'bg-blue-50',
            text: 'text-blue-500',
            border: 'border-blue-100'
        },
        night: {
            icon: Moon,
            bg: 'bg-indigo-50',
            text: 'text-indigo-500',
            border: 'border-indigo-100'
        },
        middle: {
            icon: Clock,
            bg: 'bg-teal-50',
            text: 'text-teal-500',
            border: 'border-teal-100'
        }
    };



    const userName = user?.displayName || "אורח";

    const greeting = useMemo(() => {
        const hour = new Date().getHours();
        return getGreeting(hour);
    }, []);

    async function refreshSummary() {
        try {
            const [summaryRes, historyRes] = await Promise.all([api.get("/summary"), api.get("/history")]);
            setSummary(summaryRes.data);
            if (historyRes.data.items) {
                // Showing top 4 items now that they are smaller
                const recent = historyRes.data.items.slice(0, 4);
                setRecentShifts(recent);
            }
        } catch {
            // ignore
        }
    }

    useEffect(() => {
        refreshUser();
        refreshSummary();
        api.get("/shift-types").then(res => setShiftTypes(res.data));
    }, []);

    const handleShiftSelect = (shift) => {
        setSelectedShiftCode(shift.code);
        setStartTime(shift.defaultStart);
        setEndTime(shift.defaultEnd);
    };

    const handleEditShift = (shift) => {
        setEditShiftId(shift.id);
        setSelectedDate(shift.date);
        const t = shiftTypeMap[shift.shiftType];
        if (t) setSelectedShiftCode(t.code);
        setStartTime(shift.startTime?.slice(0, 5) || "");
        setEndTime(shift.endTime?.slice(0, 5) || "");
        setOvertimeHours(shift.overtimeHours || "");
        setOvertimeRate(shift.overtimeHourlyRate || "");
        setIsOvertimeOpen(!!shift.overtimeHours);
        setIsAddOpen(true);
    };

    const handleOpenTip = (shiftId, currentTip) => {
        setTipShiftId(shiftId);
        setTipAmount(currentTip ? String(currentTip) : "");
        setIsTipOpen(true);
    };

    async function handleAddTip() {
        if (!tipShiftId || !tipAmount) return;
        try {
            await api.post(`/shifts/${tipShiftId}/tip`, {
                tipAmount: Number(tipAmount)
            });
            setIsTipOpen(false);
            setTipShiftId(null);
            setTipAmount("");
            refreshSummary();
            refreshUser();
        } catch (error) {
            console.error("Error adding tip:", error);
        }
    }

    async function handleCreateShift() {
        if (!selectedShiftCode || !selectedDate) return;
        try {
            const payload = {
                shiftCode: selectedShiftCode,
                date: selectedDate,
                startTime,
                endTime,
                overtimeHours: overtimeHours ? Number(overtimeHours) : 0,
                overtimeHourlyRate: overtimeRate
                    ? Number(overtimeRate)
                    : (overtimeHours ? overtimeRateFromSettings : null),
            };

            if (editShiftId) {
                await api.put(`/shifts/${editShiftId}`, payload);
            } else {
                await api.post("/shifts", payload);
            }

            closeAddModal();
            refreshSummary();
            refreshUser();
        } catch {
            alert("שגיאה בשמירה");
        }
    }

    const closeAddModal = () => {
        setIsAddOpen(false);
        setEditShiftId(null);
        setSelectedShiftCode(null);
        setStartTime("");
        setEndTime("");
        setIsOvertimeOpen(false);
        setOvertimeHours("");
        setOvertimeRate("");
        setSelectedDate(new Date().toISOString().slice(0, 10));
    };

    return (<Layout>
        {/* Modern Header with Glass effect feel */}
        <header className="mb-6 pt-2">
            <div className="flex flex-col gap-1 text-right">
                <h1 className="text-2xl font-bold text-slate-800 tracking-tight">
                    {greeting}, {userName}
                </h1>
                <p className="text-sm text-slate-400 font-medium">
                    {new Date().toLocaleDateString("he-IL", {
                        weekday: "long", month: "long", day: "numeric"
                    })}
                </p>
            </div>
        </header>

        {/* Bento Grid Stats */}
        <section className="mb-8">
            <div className="grid grid-cols-2 gap-3">
                {/* Main Salary Card - Spans full width on very small screens, 1 col on larger */}
                <div className="col-span-2 bg-gradient-to-br from-slate-900 to-slate-800 rounded-3xl p-6 text-white shadow-xl shadow-slate-200">
                    <p className="text-slate-300 text-sm font-medium mb-1">משכורת צפויה</p>
                    <div className="flex items-end gap-2 mb-2">
                        <span className="text-4xl font-bold tracking-tight">₪{(summary?.expectedMonthSalary ?? 0).toFixed(0)}</span>
                    </div>
                    <div className="flex items-center gap-2 text-xs bg-white/10 w-fit px-3 py-1.5 rounded-full">
                        <Coins className="w-3 h-3 text-yellow-300" />
                        <span className="text-slate-200">כולל ₪{(summary?.totalTips ?? 0).toFixed(0)} טיפים</span>
                    </div>
                </div>

                <div className="bg-white p-4 rounded-3xl shadow-sm border border-slate-100">
                    <p className="text-slate-400 text-xs font-semibold mb-1">שעות שבועיות</p>
                    <p className="text-2xl font-bold text-slate-800">{(summary?.weekHours ?? 0).toFixed(1)}</p>
                </div>
                <div className="bg-white p-4 rounded-3xl shadow-sm border border-slate-100">
                    <p className="text-slate-400 text-xs font-semibold mb-1">שעות חודשיות</p>
                    <p className="text-2xl font-bold text-emerald-600">{(summary?.monthHours ?? 0).toFixed(1)}</p>
                </div>
            </div>
        </section>

        {/* Recent Shifts - List View */}
        <section>
            <div className="flex items-center justify-between mb-4">
                <h2 className="text-lg font-bold text-slate-800">
                    פעילות אחרונה
                </h2>
                {/* Optional: Link to full history */}
                {/* <button className="text-xs text-violet-600 font-semibold">הכל</button> */}
            </div>

            <div className="flex flex-col gap-3">
                {recentShifts.map((item) => {
                    const now = dayjs();
                    let ongoing = false;

                    if (item.isEnded || endedLocalIds.includes(item.id)) {
                        ongoing = false;
                    } else {
                        const formatTime = (t) => Array.isArray(t) ? `${String(t[0]).padStart(2, '0')}:${String(t[1]).padStart(2, '0')}` : (t?.slice(0, 5) || "");
                        const formatDate = (d) => Array.isArray(d) ? `${d[0]}-${String(d[1]).padStart(2, '0')}-${String(d[2]).padStart(2, '0')}` : d;

                        const dateStr = formatDate(item.date);
                        const sTime = formatTime(item.startTime) || shiftTypeMap[item.shiftType]?.defaultStart;
                        const eTime = formatTime(item.endTime) || shiftTypeMap[item.shiftType]?.defaultEnd;

                        if (dateStr && sTime && eTime) {
                            const start = dayjs(`${dateStr}T${sTime}`);
                            let end = dayjs(`${dateStr}T${eTime}`);
                            if (end.isBefore(start)) end = end.add(1, "day");
                            ongoing = now.isAfter(start) && now.add(5, 'minute').isBefore(end);
                        }
                    }

                    // Determine Style Config
                    const config = shiftConfig[item.shiftType?.toLowerCase()] || shiftConfig.middle;
                    const Icon = config.icon || Clock;

                    return (
                        <div
                            key={item.id}
                            className={`relative group bg-white rounded-2xl p-4 transition-all duration-200 
                                ${ongoing
                                ? 'shadow-lg shadow-green-100 ring-2 ring-green-400 ring-offset-2'
                                : 'shadow-sm hover:shadow-md border border-slate-100'}`}
                        >
                            <div className="flex items-center gap-4">
                                {/* Left: Icon */}
                                <div className={`w-12 h-12 rounded-2xl flex items-center justify-center ${config.bg} ${config.text} ${ongoing ? 'animate-pulse' : ''}`}>
                                    <Icon className="w-6 h-6" />
                                </div>

                                {/* Center: Details */}
                                <div className="flex-1 min-w-0 text-right">
                                    <div className="flex items-center gap-2 mb-0.5">
                                        <h3 className="text-base font-bold text-slate-900 truncate">
                                            {item.shiftType || item.name}
                                        </h3>
                                        {ongoing && <span className="w-2 h-2 rounded-full bg-green-500 animate-pulse" />}
                                    </div>
                                    <p className="text-xs text-slate-500 font-medium truncate">
                                        {new Date(item.date).toLocaleDateString("he-IL", { day: 'numeric', month: 'numeric' })}
                                        <span className="mx-1.5 opacity-50">|</span>
                                        {item.startTime?.slice(0, 5)} - {item.endTime?.slice(0, 5)}
                                    </p>
                                </div>

                                {/* Right: Money & Action */}
                                <div className="flex flex-col items-end gap-1">
                                    <span className="text-base font-bold text-slate-900">
                                        ₪{item.salary.toFixed(0)}
                                    </span>

                                    {/* Action Row - Minimalist */}
                                    <div className="flex items-center gap-2">
                                        {/* Tip Button - Small Chip */}
                                        <button
                                            onClick={(e) => { e.stopPropagation(); handleOpenTip(item.id, item.tip); }}
                                            className={`text-[10px] font-bold px-2 py-1 rounded-lg transition-colors
                                                ${item.tip > 0
                                                ? 'bg-emerald-100 text-emerald-700'
                                                : 'bg-slate-50 text-slate-400 hover:bg-slate-100'}`}
                                        >
                                            {item.tip > 0 ? `+ ₪${item.tip}` : '+ טיפ'}
                                        </button>

                                        {/* End Shift Button (Only if ongoing) */}
                                        {ongoing && (
                                            <button
                                                onClick={async (e) => {
                                                    e.stopPropagation();
                                                    if (window.confirm("לסיים משמרת?")) {
                                                        const nowStr = dayjs().format("HH:mm");
                                                        setEndedLocalIds(prev => [...prev, item.id]);
                                                        setRecentShifts(prev => prev.map(s => s.id === item.id ? { ...s, endTime: nowStr, isEnded: true } : s));
                                                        await api.post(`/shifts/${item.id}/end`);
                                                        refreshSummary(); refreshUser();
                                                    }
                                                }}
                                                className="text-[10px] bg-red-50 text-red-600 px-2 py-1 rounded-lg font-bold"
                                            >
                                                סיים
                                            </button>
                                        )}

                                        {/* Menu Trigger */}
                                        <div className="relative">
                                            <button
                                                onClick={(e) => {
                                                    e.stopPropagation();
                                                    setActiveMenuId(activeMenuId === item.id ? null : item.id);
                                                }}
                                                className="p-1.5 -mr-2 rounded-lg text-slate-300 hover:text-slate-600 hover:bg-slate-50 transition-colors"
                                            >
                                                <MoreVertical className="w-4 h-4" />
                                            </button>

                                            {/* Dropdown Menu */}
                                            {activeMenuId === item.id && (
                                                <div className="absolute left-0 top-6 w-40 bg-white rounded-xl shadow-xl border border-slate-100 z-20 overflow-hidden text-right">
                                                    <button
                                                        onClick={(e) => { e.stopPropagation(); setActiveMenuId(null); handleEditShift(item); }}
                                                        className="w-full px-4 py-2.5 text-xs font-medium text-slate-700 hover:bg-slate-50 flex items-center justify-end gap-2"
                                                    >
                                                        ערוך
                                                        <Pencil className="w-3 h-3" />
                                                    </button>
                                                    <button
                                                        onClick={async (e) => {
                                                            e.stopPropagation();
                                                            setActiveMenuId(null);
                                                            if (window.confirm("למחוק?")) {
                                                                await api.delete(`/shifts/${item.id}`);
                                                                refreshSummary(); refreshUser();
                                                            }
                                                        }}
                                                        className="w-full px-4 py-2.5 text-xs font-medium text-red-600 hover:bg-red-50 flex items-center justify-end gap-2"
                                                    >
                                                        מחק
                                                        <Trash2 className="w-3 h-3" />
                                                    </button>
                                                </div>
                                            )}
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    );
                })}

                {recentShifts.length === 0 && (
                    <div className="text-center py-10 bg-slate-50 rounded-2xl border border-dashed border-slate-200">
                        <p className="text-sm text-slate-400">אין משמרות לאחרונה</p>
                    </div>
                )}
            </div>
        </section>

        {/* FAB */}
        <button
            onClick={() => setIsAddOpen(true)}
            className="fixed bottom-6 left-6 bg-slate-900 text-white p-4 rounded-full shadow-lg shadow-slate-900/30 hover:scale-105 active:scale-95 transition-all z-40"
        >
            <Plus className="w-6 h-6" />
        </button>

        {/* Add Shift Modal */}
        {
            isAddOpen && (
                <div className="fixed inset-0 bg-black/40 backdrop-blur-sm flex items-end justify-center z-50">
                    <div className="bg-white rounded-t-3xl max-w-md w-full px-6 pt-6 pb-8 max-h-[90vh] overflow-y-auto">
                        <div className="flex items-center justify-between mb-6">
                            <h2 className="text-xl font-bold text-slate-900">
                                {editShiftId ? 'עריכת משמרת' : 'הוספת משמרת חדשה'}
                            </h2>
                            <button
                                onClick={closeAddModal}
                                className="text-slate-400 hover:text-slate-600 transition-colors"
                            >
                                <X className="w-5 h-5" />
                            </button>
                        </div>

                        <div className="mb-6">
                            <label className="block text-sm font-medium text-slate-600 mb-2 text-right">
                                תאריך
                            </label>
                            <div className="relative w-full">
                                <DatePicker
                                    selected={selectedDate ? new Date(selectedDate) : null}
                                    onChange={(date) => {
                                        if (date) {
                                            const offsetDate = new Date(date.getTime() - (date.getTimezoneOffset() * 60000));
                                            setSelectedDate(offsetDate.toISOString().slice(0, 10));
                                        } else {
                                            setSelectedDate("");
                                        }
                                    }}
                                    dateFormat="dd-MM-yyyy"
                                    className="w-full rounded-xl border border-slate-200 px-4 py-3 text-sm text-right focus:border-violet-500 focus:ring-2 focus:ring-violet-500/20 outline-none transition-all cursor-pointer"
                                    placeholderText="בחר תאריך"
                                    wrapperClassName="w-full"
                                />
                            </div>
                        </div>

                        <div className="mb-6">
                            <p className="text-sm font-medium text-slate-600 mb-3 text-right">
                                בחר סוג משמרת
                            </p>

                            {shiftTypes.length > 0 ? (<div className="grid grid-cols-2 gap-4">
                                {shiftTypes.map((shift) => {
                                    const config = shiftConfig[shift.code?.toLowerCase()] || {
                                        icon: Clock, gradient: 'from-slate-400 to-slate-500',
                                    };

                                    const Icon = config.icon;
                                    const isSelected = selectedShiftCode === shift.code;

                                    return (<button
                                        key={shift.code}
                                        type="button"
                                        onClick={() => handleShiftSelect(shift)}
                                        className={`relative flex flex-col items-center justify-center rounded-2xl p-5 border-2 transition-all duration-300 ${isSelected ? `bg-gradient-to-br ${config.gradient} text-white border-transparent shadow-lg scale-[1.02]` : 'bg-white text-slate-700 border-slate-100 hover:border-slate-200 shadow-sm'}`}
                                    >
                                        <div
                                            className={`mb-3 p-2.5 rounded-full ${isSelected ? 'bg-white/20' : 'bg-slate-50'}`}>
                                            <Icon
                                                className={`w-6 h-6 ${isSelected ? 'text-white' : 'text-slate-400'}`} />
                                        </div>
                                        <div className="font-bold text-sm whitespace-nowrap">
                                            {shift.nameHe}
                                        </div>
                                    </button>);
                                })}
                            </div>) : (<div className="text-center py-8">
                                <p className="text-slate-400 text-sm">טוען סוגי משמרות...</p>
                            </div>)}
                        </div>

                        <p className="text-sm font-medium text-slate-600 mb-3 text-right">משמרת מותאמת אישית</p>
                        <div className="grid grid-cols-2 gap-4 mb-6">
                            <div>
                                <label className="text-sm font-medium text-slate-600 block mb-2 text-right">שעת
                                    התחלה</label>
                                <input
                                    type="time"
                                    value={startTime}
                                    onChange={(e) => setStartTime(e.target.value)}
                                    className="w-full text-center text-lg h-12 rounded-xl border border-slate-200 focus:border-violet-500 focus:ring-2 focus:ring-violet-500/20 outline-none transition-all px-4"
                                    dir="ltr"
                                    step="60"
                                />
                            </div>
                            <div>
                                <label className="text-sm font-medium text-slate-600 block mb-2 text-right">שעת
                                    סיום</label>
                                <input
                                    type="time"
                                    value={endTime}
                                    onChange={(e) => setEndTime(e.target.value)}
                                    className="w-full text-center text-lg h-12 rounded-xl border border-slate-200 focus:border-violet-500 focus:ring-2 focus:ring-violet-500/20 outline-none transition-all px-4"
                                    dir="ltr"
                                    step="60"
                                />
                            </div>
                        </div>

                        {!isOvertimeOpen && (<button
                            type="button"
                            onClick={() => setIsOvertimeOpen(true)}
                            className="w-full mb-6 rounded-xl border-2 border-dashed border-slate-300 bg-slate-50/50 text-slate-600 py-3 text-sm font-medium hover:border-violet-400 hover:bg-violet-50/50 hover:text-violet-700 transition-all"
                        >
                            + הוסף שעות נוספות
                        </button>)}

                        {isOvertimeOpen && (<div
                            className="mb-6 p-4 rounded-xl bg-gradient-to-br from-amber-50 to-orange-50 border border-amber-200">
                            <div className="flex items-center justify-between mb-3">
                                <p className="text-sm font-medium text-amber-900 text-right">שעות נוספות</p>
                                <button
                                    type="button"
                                    onClick={() => {
                                        setIsOvertimeOpen(false);
                                        setOvertimeHours('');
                                        setOvertimeRate('');
                                    }}
                                    className="text-xs text-amber-600 hover:text-amber-800 font-medium"
                                >
                                    הסר
                                </button>
                            </div>
                            <div className="grid grid-cols-2 gap-3">
                                <div className="flex flex-col">
                                    <span className="text-xs font-medium text-amber-800 mb-2 text-right">
                                        שעות נוספות
                                    </span>
                                    <input
                                        type="number"
                                        min="0"
                                        step="0.25"
                                        value={overtimeHours}
                                        onChange={(e) => setOvertimeHours(e.target.value)}
                                        className="rounded-xl border border-amber-200 bg-white px-3 py-2 text-sm text-right focus:border-amber-400 focus:ring-2 focus:ring-amber-400/20 outline-none"
                                        placeholder="0"
                                    />
                                </div>
                                <div className="flex flex-col">
                                    <span className="text-xs font-medium text-amber-800 mb-2 text-right">
                                        שכר לשעה (₪)
                                    </span>
                                    <input
                                        type="number"
                                        min="0"
                                        step="1"
                                        value={overtimeRate || overtimeRateFromSettings}
                                        onChange={(e) => setOvertimeRate(e.target.value)}
                                        className="rounded-xl border border-amber-200 bg-white px-3 py-2 text-sm text-right focus:border-amber-400 focus:ring-2 focus:ring-amber-400/20 outline-none"
                                        placeholder={overtimeRateFromSettings || '0'}
                                    />
                                </div>
                            </div>
                        </div>)}

                        <button
                            type="button"
                            onClick={handleCreateShift}
                            className="w-full rounded-xl bg-gradient-to-r from-violet-600 to-purple-600 text-white py-4 text-base font-semibold shadow-lg shadow-violet-500/30 disabled:opacity-50 disabled:cursor-not-allowed hover:from-violet-700 hover:to-purple-700 transition-all"
                            disabled={!selectedShiftCode || !startTime || !endTime}
                        >
                            שמור משמרת
                        </button>
                    </div>
                </div>)
        }

        {/* Tip Modal */}
        {isTipOpen && (
            <div className="fixed inset-0 bg-slate-900/20 backdrop-blur-sm flex items-center justify-center z-50 p-4">
                <div className="bg-white rounded-3xl w-full max-w-xs p-6 shadow-2xl">
                    <div className="text-center mb-6">
                        <h3 className="text-lg font-bold text-slate-800">כמה טיפים עשית?</h3>
                    </div>
                    <div className="relative mb-6">
                        <span className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400 font-bold">₪</span>
                        <input
                            type="number"
                            value={tipAmount}
                            onChange={(e) => setTipAmount(e.target.value)}
                            className="w-full bg-slate-50 border border-slate-200 rounded-2xl py-4 px-4 text-center text-3xl font-bold text-slate-800 focus:ring-4 focus:ring-emerald-500/20 outline-none"
                            placeholder="0"
                            autoFocus
                        />
                    </div>
                    <div className="flex gap-3">
                        <button onClick={() => setIsTipOpen(false)} className="flex-1 py-3 rounded-xl font-bold text-slate-500 hover:bg-slate-50">ביטול</button>
                        <button onClick={handleAddTip} className="flex-1 bg-emerald-500 text-white py-3 rounded-xl font-bold shadow-lg shadow-emerald-500/30">שמור</button>
                    </div>
                </div>
            </div>
        )}
    </Layout >);
}
