import { useEffect, useState } from "react";
import { User, DollarSign, LogOut, Save, UserCircle } from "lucide-react";
import Layout from "../components/Layout";
import { useAuth } from "../context/AuthContext";
import api from "../api/client";

export default function SettingsPage() {
  const { user, refreshUser, logout } = useAuth();

  const [hourlyRate, setHourlyRate] = useState(51);
  const [overtimeHourlyRate, setOvertimeHourlyRate] = useState(63.75);
  const [displayName, setDisplayName] = useState("");
  const [isSaving, setIsSaving] = useState(false);
  const [isSavingName, setIsSavingName] = useState(false);

  useEffect(() => {
    async function load() {
      try {
        const res = await api.get("/settings");
        // Backend now defaults to 51/63.75 for new users, but we also handled 0.0 case in backend.
        // We use 'hourlyRate' as per backend API, NOT 'baseHourlyRate'.
        const fetchedRate = res.data.hourlyRate;
        setHourlyRate(fetchedRate);

        // If overtime is missing or 0, auto-calc, otherwise use fetched
        const fetchedOvertime = res.data.overtimeHourlyRate;
        if (!fetchedOvertime) {
          setOvertimeHourlyRate(fetchedRate * 1.25);
        } else {
          setOvertimeHourlyRate(fetchedOvertime);
        }

        if (user) {
          setDisplayName(user.displayName || "");
        }
      } catch {
        // quiet fail
      }
    }
    load();
  }, [user]);

  function handleRateChange(e) {
    const val = e.target.value;
    setHourlyRate(val);
    // Auto-calculate overtime when base rate changes (optional UX choice, highly requested)
    if (val) {
      setOvertimeHourlyRate((Number(val) * 1.25).toFixed(2));
    }
  }

  async function handleSaveSalary() {
    setIsSaving(true);
    try {
      await api.post("/settings", {
        hourlyRate: Number(hourlyRate),
        overtimeHourlyRate: Number(overtimeHourlyRate),
      });
    } catch (e) {
      console.error(e);
    } finally {
      setIsSaving(false);
    }
  }

  async function handleSaveName() {
    if (!displayName.trim()) return;
    setIsSavingName(true);
    try {
      await api.post("/me/display-name", { displayName: displayName.trim() });
      await refreshUser();
    } catch {
      // ignore
    } finally {
      setIsSavingName(false);
    }
  }

  return (
    <Layout>
      <header className="mb-8">
        <h1 className="text-2xl font-bold text-slate-900 text-right">
          הגדרות
        </h1>
        <p className="text-sm text-slate-500 text-right mt-1">
          ניהול פרופיל והגדרות שכר
        </p>
      </header>

      <div className="space-y-6">
        {/* Profile Card */}
        <section className="bg-white rounded-3xl shadow-[0_2px_20px_-4px_rgba(0,0,0,0.05)] border border-slate-100 overflow-hidden">
          <div className="p-6 border-b border-slate-50">
            <div className="flex items-center justify-end gap-3 mb-1">
              <h2 className="text-lg font-bold text-slate-800">פרופיל אישי</h2>
              <div className="p-2 bg-violet-50 rounded-xl">
                <UserCircle className="w-5 h-5 text-violet-600" />
              </div>
            </div>
            <p className="text-xs text-slate-400 text-right">פרטי התצוגה שלך במערכת</p>
          </div>

          <div className="p-6">
            <label className="block text-sm font-medium text-slate-700 mb-2 text-right">
              שם תצוגה
            </label>
            <div className="flex gap-3">
              <button
                onClick={handleSaveName}
                disabled={isSavingName || !displayName.trim()}
                className="px-6 py-3 bg-violet-600 text-white text-sm font-semibold rounded-xl hover:bg-violet-700 disabled:opacity-50 transition-all shadow-sm shadow-violet-200"
              >
                {isSavingName ? 'שומר...' : 'שמור'}
              </button>
              <input
                type="text"
                value={displayName}
                onChange={(e) => setDisplayName(e.target.value)}
                className="flex-1 bg-slate-50 border border-slate-200 text-slate-900 text-right text-sm rounded-xl focus:ring-2 focus:ring-violet-500/20 focus:border-violet-500 block w-full p-3 outline-none transition-all"
                placeholder="הכנס שם מלא"
              />
            </div>
          </div>
        </section>

        {/* Salary Settings Card */}
        <section className="bg-white rounded-3xl shadow-[0_2px_20px_-4px_rgba(0,0,0,0.05)] border border-slate-100 overflow-hidden">
          <div className="p-6 border-b border-slate-50">
            <div className="flex items-center justify-end gap-3 mb-1">
              <h2 className="text-lg font-bold text-slate-800">הגדרות שכר</h2>
              <div className="p-2 bg-emerald-50 rounded-xl">
                <DollarSign className="w-5 h-5 text-emerald-600" />
              </div>
            </div>
            <p className="text-xs text-slate-400 text-right">הגדרת בסיס לחישוב המשכורת</p>
          </div>

          <div className="p-6 space-y-6">
            {/* Hourly Rate */}
            <div>
              <label className="block text-sm font-medium text-slate-700 mb-2 text-right">
                שכר שעתי (בסיס)
              </label>
              <div className="relative">
                <input
                  type="number"
                  step="0.1"
                  value={hourlyRate}
                  onChange={handleRateChange}
                  className="bg-slate-50 border border-slate-200 text-slate-900 text-right text-lg font-medium rounded-xl focus:ring-2 focus:ring-emerald-500/20 focus:border-emerald-500 block w-full p-4 pl-12 outline-none transition-all"
                  placeholder="51.00"
                />
                <div className="absolute inset-y-0 left-0 pl-4 flex items-center pointer-events-none">
                  <span className="text-slate-400 font-medium">₪</span>
                </div>
              </div>
            </div>

            {/* Overtime Rate */}
            <div>
              <label className="block text-sm font-medium text-slate-700 mb-2 text-right">
                שכר שעות נוספות (125%)
              </label>
              <div className="relative">
                <input
                  type="number"
                  step="0.1"
                  value={overtimeHourlyRate}
                  onChange={(e) => setOvertimeHourlyRate(e.target.value)}
                  className="bg-slate-50 border border-slate-200 text-slate-900 text-right text-lg font-medium rounded-xl focus:ring-2 focus:ring-emerald-500/20 focus:border-emerald-500 block w-full p-4 pl-12 outline-none transition-all"
                  placeholder="63.75"
                />
                <div className="absolute inset-y-0 left-0 pl-4 flex items-center pointer-events-none">
                  <span className="text-slate-400 font-medium">₪</span>
                </div>
              </div>
            </div>

            <button
              onClick={handleSaveSalary}
              disabled={isSaving}
              className="w-full flex items-center justify-center gap-2 bg-emerald-600 hover:bg-emerald-700 text-white text-base font-semibold py-4 rounded-xl shadow-lg shadow-emerald-200 transition-all disabled:opacity-70"
            >
              <Save className="w-5 h-5" />
              {isSaving ? 'שומר הגדרות...' : 'שמור הגדרות שכר'}
            </button>
          </div>
        </section>

        {/* Logout Button */}
        <button
          onClick={logout}
