import { useEffect, useMemo, useState } from "react";
import { Sun, Sunset, Moon, Clock, X, Plus } from "lucide-react";
import Layout from "../components/Layout";
import StatCard from "../components/StatCard";
import { useAuth } from "../context/AuthContext";
import api from "../api/client";

function getGreeting(hour) {
  if (hour >= 5 && hour < 12) return "בוקר טוב";
  if (hour >= 12 && hour < 17) return "צהריים טובים";
  if (hour >= 17 && hour < 22) return "ערב טוב";
  return "לילה טוב";
}

export default function HomePage() {
  const { user, refreshUser } = useAuth();
  const [summary, setSummary] = useState(null);
  const [shiftTypes, setShiftTypes] = useState([]);
  const [isAddOpen, setIsAddOpen] = useState(false);
  const [selectedShiftCode, setSelectedShiftCode] = useState(null);
  const [selectedDate, setSelectedDate] = useState(
      new Date().toISOString().slice(0, 10)
  );
  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");
  const [isOvertimeOpen, setIsOvertimeOpen] = useState(false);
  const [overtimeHours, setOvertimeHours] = useState("");
  const [overtimeRate, setOvertimeRate] = useState("");
  const [overtimeRateFromSettings, setOvertimeRateFromSettings] = useState(0);

  useEffect(() => {
    async function load() {
      try {
        refreshUser();

        // Load data with individual error handling
        const summaryPromise = api.get("/summary").catch(() => null);
        const typesPromise = api.get("/shift-types").catch((err) => {
          console.error("Error loading shift types:", err);
          // Return default shift types if API fails
          return {
            data: [
              { code: 'morning', nameHe: 'בוקר', defaultStart: '06:00', defaultEnd: '14:00', emoji: Sun },
              { code: 'evening', nameHe: 'ערב', defaultStart: '14:00', defaultEnd: '22:00', emoji: Sunset },
              { code: 'night', nameHe: 'לילה', defaultStart: '22:00', defaultEnd: '06:00', emoji: Moon }
            ]
          };
        });
        const settingsPromise = api.get("/settings").catch(() => ({ data: { overtimeHourlyRate: 0 } }));

        const [summaryRes, typesRes, settingsRes] = await Promise.all([
          summaryPromise,
          typesPromise,
          settingsPromise
        ]);

        if (summaryRes) setSummary(summaryRes.data);
        console.log("Shift types loaded:", typesRes.data);
        setShiftTypes(typesRes.data || []);
        setOvertimeRateFromSettings(settingsRes.data.overtimeHourlyRate || 0);
      } catch (e) {
        console.error("Error loading data:", e);
        // Set default shift types as fallback
        setShiftTypes([
          { code: 'morning', nameHe: 'משמרת בוקר', defaultStart: '06:30', defaultEnd: '15:30' },
          { code: 'evening', nameHe: 'משמרת ערב', defaultStart: '14:30', defaultEnd: '23:15' },
          { code: 'night', nameHe: 'משמרת לילה', defaultStart: '22:30', defaultEnd: '07:15' },
          { code: 'middle', nameHe: 'משמרת מידל', defaultStart: '11:30', defaultEnd: '20:30' }
        ]);
      }
    }
    load();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const shiftConfig = {
    morning: {
      icon: Sun,
      gradient: 'from-yellow-300 to-cyan-500',
      hoverGradient: 'from-amber-600 to-orange-600'
    },
    evening: {
      icon: Sunset,
      gradient: 'from-orange-500 to-rose-500',
      hoverGradient: 'from-orange-600 to-rose-600'
    },
    night: {
      icon: Moon,
      gradient: 'from-indigo-500 to-purple-500',
      hoverGradient: 'from-indigo-600 to-purple-600'
    },
    middle: {
      icon: Clock,
      gradient: 'from-teal-400 to-emerald-500',
    }
  };

  const greeting = useMemo(() => {
    const hour = new Date().getHours();
    return getGreeting(hour);
  }, []);

  const userName = user?.displayName || "אורח";

  async function refreshSummary() {
    try {
      const res = await api.get("/summary");
      setSummary(res.data);
    } catch {
      // ignore
    }
  }

  const calculateHours = (start, end) => {
    if (!start || !end) return 0;
    const [startH, startM] = start.split(':').map(Number);
    const [endH, endM] = end.split(':').map(Number);
    let hours = endH - startH;
    let minutes = endM - startM;
    if (minutes < 0) {
      hours -= 1;
      minutes += 60;
    }
    if (hours < 0) hours += 24;
    return hours + minutes / 60;
  };

  const totalHours = calculateHours(startTime, endTime);

  const handleShiftSelect = (shift) => {
    setSelectedShiftCode(shift.code);
    setStartTime(shift.defaultStart);
    setEndTime(shift.defaultEnd);
  };

  async function handleCreateShift() {
    if (!selectedShiftCode || !selectedDate) return;
    try {
      await api.post("/shifts", {
        shiftCode: selectedShiftCode,
        date: selectedDate,
        overtimeHours: overtimeHours ? Number(overtimeHours) : 0,
        overtimeHourlyRate: overtimeRate ? Number(overtimeRate) : (overtimeHours ? overtimeRateFromSettings : null),
      });
      setIsAddOpen(false);
      setSelectedShiftCode(null);
      setStartTime("");
      setEndTime("");
      setIsOvertimeOpen(false);
      setOvertimeHours("");
      setOvertimeRate("");
      await refreshSummary();
      await refreshUser();
    } catch {
      // likely not logged in yet
    }
  }

  function formatDateForDisplay(dateStr) {
    const date = new Date(dateStr);
    const day = String(date.getDate()).padStart(2, "0");
    const month = String(date.getMonth() + 1).padStart(2, "0");
    const year = date.getFullYear();
    return `${day}-${month}-${year}`;
  }

  return (
      <Layout>
        <header className="mb-6">
          <div className="flex flex-col gap-1 text-right">
            <h1 className="text-xl font-semibold text-slate-900">
              {greeting}, {userName}
            </h1>
            <p className="text-xs text-slate-500">
              {new Date().toLocaleDateString("he-IL", {
                weekday: "long",
                month: "long",
                day: "numeric",
                year: "numeric",
              })}
            </p>
          </div>
        </header>

        <section className="space-y-3 mb-4">
          <div className="grid grid-cols-2 gap-3">
            <StatCard
                title="שעות החודש"
                value={(summary?.monthHours ?? 0).toFixed(1)}
                tone="green"
            />
            <StatCard
                title="שעות השבוע"
                value={(summary?.weekHours ?? 0).toFixed(1)}
                tone="primary"
            />
          </div>
          <StatCard
              title="משכורת צפויה לחודש"
              subtitle={`שכר לשעה ₪${summary?.hourlyRate ?? 0}`}
              value={`₪${(summary?.expectedMonthSalary ?? 0).toFixed(0)}`}
              tone="dark"
              align="center"
          />
        </section>

        <section className="mt-6">
          <h2 className="text-sm font-semibold text-slate-800 mb-3">
            משמרות אחרונות
          </h2>
          <div className="space-y-3">
            {(summary?.recentShifts ?? []).map((shift) => (
                <article
                    key={shift.id}
                    className="rounded-2xl bg-amber-50 border border-amber-100 px-4 py-3 flex items-center justify-between"
                >
                  <div className="flex flex-col items-end gap-1">
                <span className="text-xs text-amber-600 font-medium">
                  {shift.shiftType}
                </span>
                    <span className="text-[10px] text-slate-500">
                  {new Date(shift.date).toLocaleDateString("he-IL")}
                </span>
                  </div>
                  <div className="flex flex-col items-start gap-1">
                <span className="text-xs text-slate-700">
                  שעות {shift.hours.toFixed(1)}
                </span>
                    <span className="text-[10px] text-slate-500">
                  {shift.startTime} - {shift.endTime}
                </span>
                  </div>
                </article>
            ))}
            {(summary?.recentShifts?.length ?? 0) === 0 && (
                <p className="text-xs text-slate-400 text-center">
                  עדיין אין משמרות שמורות. התחבר כדי להתחיל לעקוב.
                </p>
            )}
          </div>
        </section>

        {/* Floating Action Button */}
        <button
            onClick={() => setIsAddOpen(true)}
            className="fixed bottom-20 left-1/2 -translate-x-1/2 bg-gradient-to-r from-violet-600 to-purple-600 text-white px-8 py-4 rounded-2xl shadow-xl shadow-violet-500/30 flex items-center gap-3 font-semibold z-40 hover:from-violet-700 hover:to-purple-700 transition-all"
        >
          <Plus className="w-5 h-5" />
          הוסף משמרת
        </button>

        {/* Modal */}
        {isAddOpen && (
            <div className="fixed inset-0 bg-black/40 backdrop-blur-sm flex items-end justify-center z-50">
              <div className="bg-white rounded-t-3xl max-w-md w-full px-6 pt-6 pb-8">
                {/* Header */}
                <div className="flex items-center justify-between mb-6">
                  <h2 className="text-xl font-bold text-slate-900">
                    הוספת משמרת חדשה
                  </h2>
                  <button
                      onClick={() => setIsAddOpen(false)}
                      className="text-slate-400 hover:text-slate-600 transition-colors"
                  >
                    <X className="w-5 h-5" />
                  </button>
                </div>

                {/* Date Input */}
                <div className="mb-6">
                  <label className="block text-sm font-medium text-slate-600 mb-2 text-right">
                    תאריך
                  </label>
                  <input
                      type="date"
                      value={selectedDate}
                      onChange={(e) => setSelectedDate(e.target.value)}
                      className="w-full rounded-xl border border-slate-200 px-4 py-3 text-sm text-left focus:border-violet-500 focus:ring-2 focus:ring-violet-500/20 outline-none transition-all"
                  />
                  <p className="text-xs text-slate-400 mt-2 text-right">
                    {formatDateForDisplay(selectedDate)}
                  </p>
                </div>

                {/* Shift Type Selector */}
                <div className="mb-6">
                  <p className="text-sm font-medium text-slate-600 mb-3 text-right">
                    בחר סוג משמרת
                  </p>

                  {shiftTypes.length > 0 ? (
                      /* Changed grid-cols-3 to grid-cols-2 for a perfect 2x2 balance */
                      <div className="grid grid-cols-2 gap-4">
                        {shiftTypes.map((shift) => {
                          const config = shiftConfig[shift.code?.toLowerCase()] || {
                            icon: Clock,
                            gradient: 'from-slate-400 to-slate-500',
                          };

                          const Icon = config.icon;
                          const isSelected = selectedShiftCode === shift.code;

                          return (
                              <button
                                  key={shift.code}
                                  type="button"
                                  onClick={() => handleShiftSelect(shift)}
                                  className={`relative flex flex-col items-center justify-center rounded-2xl p-5 border-2 transition-all duration-300 ${
                                      isSelected
                                          ? `bg-gradient-to-br ${config.gradient} text-white border-transparent shadow-lg scale-[1.02]`
                                          : 'bg-white text-slate-700 border-slate-100 hover:border-slate-200 shadow-sm'
                                  }`}
                              >
                                {/* Icon Container */}
                                <div className={`mb-3 p-2.5 rounded-full ${isSelected ? 'bg-white/20' : 'bg-slate-50'}`}>
                                  <Icon className={`w-6 h-6 ${isSelected ? 'text-white' : 'text-slate-400'}`} />
                                </div>

                                {/* Shift Name */}
                                <div className="font-bold text-sm whitespace-nowrap">
                                  {shift.nameHe}
                                </div>

                                {/* Selection indicator (Top-Right dot) */}
                                {isSelected && (
                                    <div className="absolute top-3 right-3 w-2 h-2 bg-white rounded-full shadow-sm" />
                                )}
                              </button>
                          );
                        })}
                      </div>
                  ) : (
                      <div className="text-center py-8">
                        <p className="text-slate-400 text-sm">טוען סוגי משמרות...</p>
                      </div>
                  )}
                </div>

                {/* Time Inputs */}
                <div className="grid grid-cols-2 gap-4 mb-6">
                  <div>
                    <label className="text-sm font-medium text-slate-600 block mb-2 text-right">שעת התחלה</label>
                    <input
                        type="time"
                        value={startTime}
                        onChange={(e) => setStartTime(e.target.value)}
                        className="w-full text-center text-lg h-12 rounded-xl border border-slate-200 focus:border-violet-500 focus:ring-2 focus:ring-violet-500/20 outline-none transition-all"
                    />
                  </div>
                  <div>
                    <label className="text-sm font-medium text-slate-600 block mb-2 text-right">שעת סיום</label>
                    <input
                        type="time"
                        value={endTime}
                        onChange={(e) => setEndTime(e.target.value)}
                        className="w-full text-center text-lg h-12 rounded-xl border border-slate-200 focus:border-violet-500 focus:ring-2 focus:ring-violet-500/20 outline-none transition-all"
                    />
                  </div>
                </div>

                {/* Hours Preview */}
                {startTime && endTime && (
                    <div className="bg-gradient-to-br from-slate-50 to-slate-100 rounded-xl p-4 text-center mb-6 border border-slate-200">
                      <p className="text-slate-500 text-sm mb-1">סה״כ שעות</p>
                      <p className="text-4xl font-bold text-slate-800">
                        {totalHours.toFixed(1)}
                      </p>
                    </div>
                )}

                {/* Overtime Section */}
                {!isOvertimeOpen && (
                    <button
                        type="button"
                        onClick={() => setIsOvertimeOpen(true)}
                        className="w-full mb-6 rounded-xl border-2 border-dashed border-slate-300 bg-slate-50/50 text-slate-600 py-3 text-sm font-medium hover:border-violet-400 hover:bg-violet-50/50 hover:text-violet-700 transition-all"
                    >
                      + הוסף שעות נוספות
                    </button>
                )}

                {isOvertimeOpen && (
                    <div className="mb-6 p-4 rounded-xl bg-gradient-to-br from-amber-50 to-orange-50 border border-amber-200">
                      <div className="flex items-center justify-between mb-3">
                        <p className="text-sm font-medium text-amber-900 text-right">שעות נוספות</p>
                        <button
                            type="button"
                            onClick={() => {
                              setIsOvertimeOpen(false);
                              setOvertimeHours('');
                              setOvertimeRate('');
                            }}
                            className="text-xs text-amber-600 hover:text-amber-800 font-medium"
                        >
                          הסר
                        </button>
                      </div>
                      <div className="grid grid-cols-2 gap-3">
                        <div className="flex flex-col">
                    <span className="text-xs font-medium text-amber-800 mb-2 text-right">
                      שעות נוספות
                    </span>
                          <input
                              type="number"
                              min="0"
                              step="0.25"
                              value={overtimeHours}
                              onChange={(e) => setOvertimeHours(e.target.value)}
                              className="rounded-xl border border-amber-200 bg-white px-3 py-2 text-sm text-left focus:border-amber-400 focus:ring-2 focus:ring-amber-400/20 outline-none"
                              placeholder="0"
                          />
                        </div>
                        <div className="flex flex-col">
                    <span className="text-xs font-medium text-amber-800 mb-2 text-right">
                      שכר לשעה (₪)
                    </span>
                          <input
                              type="number"
                              min="0"
                              step="1"
                              value={overtimeRate || overtimeRateFromSettings}
                              onChange={(e) => setOvertimeRate(e.target.value)}
                              className="rounded-xl border border-amber-200 bg-white px-3 py-2 text-sm text-left focus:border-amber-400 focus:ring-2 focus:ring-amber-400/20 outline-none"
                              placeholder={overtimeRateFromSettings || '0'}
                          />
                        </div>
                      </div>
                    </div>
                )}

                {/* Submit Button */}
                <button
                    type="button"
                    onClick={handleCreateShift}
                    className="w-full rounded-xl bg-gradient-to-r from-violet-600 to-purple-600 text-white py-4 text-base font-semibold shadow-lg shadow-violet-500/30 disabled:opacity-50 disabled:cursor-not-allowed hover:from-violet-700 hover:to-purple-700 transition-all"
                    disabled={!selectedShiftCode || !startTime || !endTime}
                >
                  שמור משמרת
                </button>
              </div>
            </div>
        )}
      </Layout>
  );
}
